import { Pipe, PipeTransform } from '@angular/core';
import { NzSafeAny } from 'ng-zorro-antd/core/types';

@Pipe({
  name: 'defaultify'
})
export class DefaultifyPipe implements PipeTransform {
  transform<T = NzSafeAny>(value: T | undefined | null, defaultValue?: T | string): T | string {
    return value == null ? defaultValue || '--' : value;
  }
}

import { Pipe, PipeTransform } from '@angular/core';
import { NzSafeAny } from 'ng-zorro-antd/core/types';
import { NzxUtils } from '@xmagic/nzx-antd/util';

@Pipe({
  name: 'filter'
})
export class FilterPipe implements PipeTransform {
  transform<T = NzSafeAny>(array: T[], matcher: string, searchText?: string): T[];
  transform<T = NzSafeAny>(array: T[], matcher: FilterMatcherFn<T>): T[];

  /**
   * Filter array
   *
   * 过滤数组
   */
  transform<T = NzSafeAny>(array: T[], matcher: FilterMatcherFn<T> | string, searchText?: string): T[] {
    if (typeof matcher === 'string') {
      if (NzxUtils.isEmpty(searchText)) {
        return array;
      }

      // @ts-ignore
      const text = searchText.toLowerCase();
      return (array || []).filter(v => NzxUtils.get(v, matcher, '').toLowerCase().indexOf(text) !== -1);
    }
    return (array || []).filter(v => matcher(v));
  }
}

export type FilterMatcherFn<T> = (item: T) => boolean;

import { Pipe, PipeTransform } from '@angular/core';

/**
 * 调用数学函数 如{{ 5.1 | math: 'ceil' }} => 6
 */
@Pipe({
  name: 'math'
})
export class MathPipe implements PipeTransform {
  transform(value: string | number, fnName: keyof Math, fixed?: number): string | null {
    if (value == null) {
      return null;
    }
    const n: number = typeof value === 'string' ? Number(value) : value;
    // @ts-ignore
    const result = Math[fnName](n);
    return fixed == null ? result : n.toFixed(fixed);
  }
}

import { Pipe, PipeTransform } from '@angular/core';

@Pipe({
  name: 'timeUnit'
})
export class TimeUnitPipe implements PipeTransform {
  transform(value: number | undefined | null, unit: 's' | 'ms'): string | null {
    if (value == null) {
      return null;
    }

    const second = unit === 's' ? value : Math.floor(value / 1000);
    const days = Math.floor(second / 86400);
    const hours = Math.floor((second % 86400) / 3600);
    const minutes = Math.floor(((second % 86400) % 3600) / 60);
    const seconds = Math.floor(((second % 86400) % 3600) % 60);

    const units = ['天', '小时', '分', '秒'];
    const format = [days, hours, minutes, seconds].reduce((str, v, i) => (v ? str + v + units[i] : str), '');
    return format || '0秒';
  }
}

import { Pipe, PipeTransform } from '@angular/core';
import { from, map, Observable, of } from 'rxjs';
import { FetcherService, FetchOptions } from '@xmagic/nzx-antd/service';
import { NzxUtils } from '@xmagic/nzx-antd/util';
import { NzSafeAny } from 'ng-zorro-antd/core/types';

/**
 * 把请求信息转换为异步对象
 */
@Pipe({
  name: 'toAsync'
})
export class ToAsyncPipe implements PipeTransform {
  constructor(private fetcher: FetcherService) {}

  /**
   * 转换为Observable
   * @param value 可以是url或者Observable, Promise
   * @param option 配置项
   */
  transform<T = NzSafeAny, U = NzSafeAny>(
    value: string | Observable<T> | Promise<T> | T,
    option?: AsyncOption<T, U>
  ): Observable<U> | null {
    if (value == null) {
      return of(option?.defaultValue as U);
    }

    const opt = option || ({} as AsyncOption<T, U>);
    const mapFn = NzxUtils.isFunction(opt.map) ? opt.map : (data: T) => data as unknown as U;
    if (typeof value === 'string') {
      return this.fetcher.fetch<T>({ ...opt, url: value }).pipe(
        map(v => NzxUtils.defaultIfy(v, option?.defaultValue)),
        map<T, U>(mapFn)
      );
    }

    if (NzxUtils.isPromise(value) || NzxUtils.isObservable(value)) {
      return from(value).pipe(
        map(v => NzxUtils.defaultIfy(v, option?.defaultValue)),
        map<T, U>(mapFn)
      );
    }

    return of(value as T).pipe(map<T, U>(mapFn));
  }
}

/**
 * 异步请求信息
 */
export type AsyncOption<T = NzSafeAny, U = NzSafeAny> = Omit<FetchOptions, 'url'> & {
  /**
   * 映射数据
   * @param data
   * @param index
   */
  map?: (data: T) => U;
  /**
   * 默认值
   */
  defaultValue?: U;
};

import { Pipe, PipeTransform } from '@angular/core';
import { DomSanitizer, SafeHtml, SafeResourceUrl, SafeScript, SafeStyle } from '@angular/platform-browser';

/**
 * 非安全加载URL,比如加载iframe url
 */
@Pipe({
  name: 'trustUrl'
})
export class TrustUrlPipe implements PipeTransform {
  constructor(private sanitizer: DomSanitizer) {}
  transform(url: string): SafeResourceUrl {
    return this.sanitizer.bypassSecurityTrustResourceUrl(url);
  }
}

/**
 * 非安全加载HTML
 */
@Pipe({
  name: 'trustHtml'
})
export class TrustHtmlPipe implements PipeTransform {
  constructor(private sanitizer: DomSanitizer) {}
  transform(html: string, enabled = true): SafeHtml {
    return enabled ? this.sanitizer.bypassSecurityTrustHtml(html) : html;
  }
}

/**
 * 非安全调用Script
 */
@Pipe({
  name: 'trustScript'
})
export class TrustScriptPipe implements PipeTransform {
  constructor(private sanitizer: DomSanitizer) {}
  transform(script: string, enabled = true): SafeScript {
    return enabled ? this.sanitizer.bypassSecurityTrustScript(script) : script;
  }
}

/**
 * 非安全调用Style
 */
@Pipe({
  name: 'trustStyle'
})
export class TrustStylePipe implements PipeTransform {
  constructor(private sanitizer: DomSanitizer) {}
  transform(style: string, enabled = true): SafeStyle {
    return enabled ? this.sanitizer.bypassSecurityTrustStyle(style) : style;
  }
}

