   */
  success?: (response: HttpResponse<NzSafeAny>) => boolean;
  /**
   * 是否需要默认错误的错误
   * @param error 错误信息
   */
  defaultError?: (error: HttpErrorBean) => boolean;
  /**
   * 是否登录超时
