import { Injectable } from '@angular/core';
import { Observable, Subject, Subscription } from 'rxjs';
import { NzSafeAny } from 'ng-zorro-antd/core/types'

@Injectable({ providedIn: 'root' })
export class HttpLoadingService {
  private httpLoadingStatus = new Subject<boolean>();

  constructor() {}

  /**
   * HttpLoadingService needs a subject to tell the next loading status.
   * @param statusChangeObservable The subject who tells the loading status.
   */
  init(statusChangeObservable: Observable<boolean>) {
    statusChangeObservable.subscribe(this.httpLoadingStatus);
  }

  /**
   * You have to subscribe the httpLoadingStatus$ in application to set custom loading behaviors through the method.
   * @param observer customer callbacks when http loading status change.
   *
   * @example
   * `app.component.ts`
   * ```ts
   * constructor(private loading: HttpLoadingService){}
   * ngOnInit() {
   *   this.loading.subscribe(status => {
   *     if(status){
   *       this.modal.show('loading...');
   *     }else{
   *       this.modal.hide();
   *     }
   *   })
   * }
   * ```
   */
  subscribe(next?: (value: boolean) => void, error?: (error: NzSafeAny) => void, complete?: () => void): Subscription {
    return this.httpLoadingStatus.subscribe(next, error, complete);
  }
}

import { Injectable } from '@angular/core';
import { NzxModalWrapService } from '@xmagic/nzx-antd/modal';
import { HttpError, ResponseModel } from './http.model';
import { DEFAULT_STATUS_MESSAGE_MAP, NzxAntdService } from '@xmagic/nzx-antd';
import { Subject, filter } from 'rxjs';

/**
 * 对于异常的处理
 */
@Injectable({ providedIn: 'root' })
export class HttpNotifyService {
  private isOpen = false;
  private readonly errorSubject = new Subject<string>();
  private readonly statusMessageMap: Record<string, string> = {};

  constructor(protected modal: NzxModalWrapService, protected antdService: NzxAntdService) {
    this.statusMessageMap = Object.assign({}, DEFAULT_STATUS_MESSAGE_MAP, antdService.response?.statusMessageMap || {});
    this.errorSubject.pipe(filter(() => !this.isOpen)).subscribe(message => this.showErrorModal(message));
  }

  /**
   * 通用的业务异常
   * @param error
   */
  notifyCustomServerError(error: HttpError<ResponseModel>) {
    this.errorSubject.next(error.message);
  }

  /**
   * http原始异常
   * @param error
   */
  notifyHttpOriginError(error: HttpError): void {
    this.errorSubject.next(this.statusMessageMap[error.code] || this.statusMessageMap.other);
  }

  protected showErrorModal(nzContent: string) {
    this.isOpen = true;
    this.modal
      .error({
        nzTitle: '错误',
        nzContent
      })
      .afterClose.subscribe(() => (this.isOpen = false));
  }
}

import { Injectable } from '@angular/core';
import { HttpError, ResponseModel } from './http.model';
import { Subject, Subscription } from 'rxjs';
import { NzxModalWrapService } from '@xmagic/nzx-antd/modal';
import { throttleTime } from 'rxjs/operators';
import { NzxUtils } from '@xmagic/nzx-antd/util';
import { DEFAULT_RESPONSE_SETTING, NzxAntdService, ResponseSetting } from '@xmagic/nzx-antd'

/**
 * 退出通知
 */
@Injectable({ providedIn: 'root' })
export class LogoutService {
  protected readonly timeoutFn: (error: HttpError) => boolean;
  private loginNotify$ = new Subject<LogoutType>();

  constructor(protected modal: NzxModalWrapService, protected antdService: NzxAntdService) {
    this.timeoutFn = NzxUtils.extend<ResponseSetting>({}, DEFAULT_RESPONSE_SETTING, this.antdService.response).timeout!;
  }

  notifyLogin(error: HttpError<ResponseModel>): void {
    this.loginNotify$.next({
      message: error.message,
      url: (error.body as { url?: string })?.url,
      timeout: this.timeoutFn(error)
    });
  }

  /**
   * 静默通知退出
   * @param message 提示消息
   * @param code 错误码 -1 不显示提示信息
   */
  notifyLogout(message: string = '', code = 0): void {
    this.notifyLogin(new HttpError(false, code, message, {}));
  }

  /**
   * 执行退出登录Observable
   * @param logoutType
   */
  logout(logoutType: LogoutType): void {
    this.loginNotify$.next(logoutType);
  }

  onLogout(fn: (logoutType: LogoutType) => void): Subscription {
    return this.loginNotify$.asObservable().pipe(throttleTime(2000)).subscribe(fn);
  }
}

export interface LogoutType {
  /**
   * url
   */
  url?: string;
  /**
   * 是否超时
   */
  timeout?: boolean;
  /**
   * 错误信息
   */
  message?: string;
}

import { Injectable } from '@angular/core';
import { HttpContext, HttpRequest, HttpResponse } from '@angular/common/http';
import { NzSafeAny } from 'ng-zorro-antd/core/types';
import { Observable } from 'rxjs';

@Injectable({
  providedIn: 'root'
})
export class NzxAntdService {
  /**
   * 默认请求数据类型, 默认 json
   */
  contentType?: 'form' | 'json';
  /**
   * 请求的根路径,例如/context
   */
  basePath?: string;
  /**
   * 响应的数据配置
   */
  response?: ResponseSetting;
  /**
   * 自定义处理原始请求, 返回null,void则使用默认处理器
   */
  handleRequest?: (req: HttpRequest<NzSafeAny>, url: string) => HttpRequest<NzSafeAny> | null | HttpRequestOptions;

  /**
   * 是否有权限
   */
  hasAuth?: <T = NzSafeAny>(value: T) => Observable<boolean>;
  /**
   * 权限变更, 通常切换用户时需要
   */
  authChange?: () => Observable<void>;

  /**
   * 无权限时跳转路径
   */
  noAuthUrl?: string;

  /**
   * 表格分页配置
   */
  table?: TableSetting;
  constructor() {}
}

/**
 * 默认配置
 */
export const DEFAULT_RESPONSE_SETTING: ResponseSetting = {
  code: 'code',
  message: 'message',
  data: 'result',
  // tslint:disable-next-line:triple-equals
  success: response => response.body.code == 200,
  // tslint:disable-next-line:triple-equals
  timeout: error => error.code == 401,
  forceLogout: () => false,
  defaultError: error => error.code < 1024
};

/**
 * 默认状态错误信息
 */
export const DEFAULT_STATUS_MESSAGE_MAP: Record<string, string> = {
  0: '请求网络错误，请检查网络是否正常',
  404: '请求的地址不存在，请检查地址是否正确',
  403: '您没有操作权限',
  other: '请求发生错误，请联系管理员'
};

/**
 * 响应配置
 */
export interface ResponseSetting {
  /**
   * code字段名称, 支持路径属性
   */
  code?: string;
  /**
   * message字段名称, 支持路径属性
   */
  message?: string;
  /**
   * data字段名称, 支持路径属性
   */
  data?: string;
  /**
   * http错误码和错误信息映射,比如 `{404: '请求的地址不存在，请检查地址是否正确', 500: '服务器错误'}`
   * other 表示不匹配任何错误时显示other信息
   */
  statusMessageMap?: { [status: string]: string; other: string };

  /**
   * 是否请求成功
   * @param body 响应对象
   */
  success?: (response: HttpResponse<NzSafeAny>) => boolean;
  /**
   * 是否需要默认错误的错误
   * @param error 错误信息
   */
  defaultError?: (error: HttpErrorBean) => boolean;
  /**
   * 是否登录超时
   * @param error 错误信息
   */
  timeout?: (error: HttpErrorBean) => boolean;
  /**
   * 是否强制退出登录, 比如强制下线
   * @param error 错误信息
   */
  forceLogout?: (error: HttpErrorBean) => boolean;
}

/**
 * 表格配置
 */
export interface TableSetting {
  /**
   * 请求接口当前页数
   */
  pageIndexField?: string;
  /**
   * 每页显示多少条
   */
  pageSizeField?: string;
  /**
   * 请求结果列表字段  支持 a.b.c
   */
  listField?: string;
  /**
   * 请求结果总数字段  支持 a.b.c
   */
  totalField?: string;
  /**
   * 请求方式
   */
  method?: string;
  /**
   *  相应类型
   */
  responseType?: 'arraybuffer' | 'blob' | 'json' | 'text';
  /**
   * 分页大小
   */
  nzPageSize?: number;
}

/**
 * 请求公共配置
 */
export interface HttpRequestOptions {
  context?: HttpContext;
  reportProgress?: boolean;
  responseType?: 'arraybuffer' | 'blob' | 'json' | 'text';
  withCredentials?: boolean;
  body?: NzSafeAny | null;
  method?: string;
  url?: string;
  setHeaders?: Record<string, NzSafeAny>;
  setParams?: Record<string, NzSafeAny>;
}

export interface HttpErrorBean<T = NzSafeAny> {
  httpError: boolean;
  code: number;
  message: string;
  body: T;
}

import { Injectable } from '@angular/core';
import { HttpHeaders, HttpResponse } from '@angular/common/http';
import { FetcherService, FetchOptions } from './fetcher.service';
import { NzSafeAny } from 'ng-zorro-antd/core/types'

@Injectable()
export class NzxDownloadService {
  constructor(protected fetcher: FetcherService) {}

  /**
   * 下载文件
   * @param options 下载文件配置信息
   */
  download(options: DownloadOption) {
    this.fetcher
      .fetch<HttpResponse<Blob>>({ ...options, responseType: 'blob', observe: 'response' })
      .subscribe(resp => {
        const url = options.url;
        const filename = this.getFilename(resp.headers, url);
        if (options.afterDownload && options.afterDownload(resp, filename) === false) {
          return;
        }

        this.saveAs(resp.body!, filename);
        if (options.downloadDone) {
          options.downloadDone(resp, filename);
        }
      });
  }

  /**
   * 文件另存为
   * @param body 二进制内容
   * @param filename 文件名
   */
  saveAs(body: Blob, filename: string) {
    if (typeof (window.navigator as NzSafeAny).msSaveBlob !== 'undefined') {
      (window.navigator as NzSafeAny).msSaveBlob(body, filename);
    }

    const blobURL = window.URL.createObjectURL(body as Blob);
    const tempLink = document.createElement('a');
    tempLink.style.display = 'none';
    tempLink.href = blobURL;
    tempLink.setAttribute('download', filename);
    if (typeof tempLink.download === 'undefined') {
      tempLink.setAttribute('target', '_blank');
    }
    document.body.appendChild(tempLink);
    tempLink.click();
    document.body.removeChild(tempLink);
    window.URL.revokeObjectURL(blobURL);
  }

  /**
   * 获取文件名称
   * @param headers
   * @param url
   * @protected
   */
  protected getFilename(headers: HttpHeaders, url: string): string {
    const disposition = headers.get('content-disposition');
    const filename = headers.get('filename');
    if (filename) {
      return decodeURIComponent(filename);
    } else if (disposition) {
      return disposition.split(';')[0].split('=')[1];
    } else {
      const start = url.lastIndexOf('/') + 1;
      const endIndex = url.lastIndexOf('?');
      const end = endIndex === -1 ? url.length : endIndex;
      return url.substring(start, end);
    }
  }
}

/**
 * 下载文件配置信息
 */
export type DownloadOption = Omit<FetchOptions, 'observe'> & {
  /**
   * 请求完成后的回调
   * @param resp
   * @param filename
   */
  afterDownload?: (resp: HttpResponse<Blob>, filename: string) => boolean | void;

  /**
   * 下载并保存完成的回调
   * @param resp
   * @param filename
   */
  downloadDone?: (resp: HttpResponse<Blob>, filename: string) => void;
};

import { Injectable } from '@angular/core';

@Injectable({
  providedIn: 'root'
})
export class NzxLoadingService {
  readonly loadingId = '__MESSAGE_LOADING__';

  /**
   * 显示loading
   * @param type loading类型
   * @param message 消息
   */
  show(type: 'message' | 'spin' = 'message', message: string = '加载中...') {
    this.showx(this.loadingId, type, () => {
      if (type === 'spin') {
        return `
        <div class="ant-modal-wrap" style="position: fixed; z-index: 999998; background-color: rgba(255, 255, 255, 0.35)"></div>
        <div
          class="ant-spin ant-spin-spinning ant-spin-show-text"
          style="top: 45%; transform: translate(-50%); z-index: 999999; position: fixed; left: 50%"
        >
          <span class="ant-spin-dot ant-spin-dot-spin">
            <i class="ant-spin-dot-item"></i>
            <i class="ant-spin-dot-item"></i>
            <i class="ant-spin-dot-item"></i>
            <i class="ant-spin-dot-item"></i>
          </span>
          <div class="ant-spin-text">${message}</div>
        </div>`;
      }

      return `
      <div class="ant-modal-wrap" style="z-index: 999998;background-color: rgba(255, 255, 255,.35);"></div>
      <div class="ant-message-notice ant-message" style="top: 45%; z-index: 999999;">
        <div class="ant-message-notice-content" style="min-width: 150px;">
          <div class="ant-message-loading">
            <i class="anticon anticon-loading">
              <svg viewBox="0 0 1024 1024" focusable="false" fill="currentColor" width="1em" height="1em"
                   class="anticon-spin">
                <path
                  d="M988 548c-19.9 0-36-16.1-36-36 0-59.4-11.6-117-34.6-171.3a440.45 440.45 0 00-94.3-139.9 437.71 437.71 0 00-139.9-94.3C629 83.6 571.4 72 512 72c-19.9 0-36-16.1-36-36s16.1-36 36-36c69.1 0 136.2 13.5 199.3 40.3C772.3 66 827 103 874 150c47 47 83.9 101.8 109.7 162.7 26.7 63.1 40.2 130.2 40.2 199.3.1 19.9-16 36-35.9 36z"></path>
              </svg>
            </i>
            <span>${message}</span>
          </div>
        </div>
      </div>`;
    });
  }

  private showx(id: string, type: 'message' | 'spin', html: () => string) {
    const loading = document.getElementById(id);
    if (loading) {
      if (loading.getAttribute('type') !== type) {
        loading.setAttribute('type', type);
        loading.innerHTML = html();
      }
      loading.style.display = 'block';
      return;
    }
    const div = document.createElement('div');
    div.setAttribute('type', type);
    div.setAttribute('id', id);
    div.innerHTML = html();
    document.body.append(div);
  }

  /**
   * 显示或隐藏loading
   * @param show 是否显示
   */
  loading(show: boolean = true): void {
    show ? this.show() : this.hide();
  }

  /**
   * 隐藏loading
   */
  hide() {
    const loading = document.getElementById(this.loadingId);
    if (loading) {
      loading.style.display = 'none';
    }
  }
}

export const loadingService = new NzxLoadingService();

import { Injectable } from '@angular/core';
import { DragDrop, DragRef } from '@angular/cdk/drag-drop';
import { NzSafeAny } from 'ng-zorro-antd/core/types';
import { ModalTypes, NzModalService } from 'ng-zorro-antd/modal';

/**
 * 对话框拖动服务
 */
@Injectable()
export class NzxModalDragService {
  static readonly DRAG_CLS_PREFIX = 'NZ-MODAL-WRAP-CLS-';
  constructor(public modal: NzModalService, public dragDrop: DragDrop) {}

  /**
   * 创建拖拽手柄
   * @param wrapCls 类名
   * @param nzMask 是否有遮罩
   * @param nzModalType 对话框类型
   */
  createDragHandler<T = NzSafeAny>(wrapCls: string, nzMask?: boolean, nzModalType?: ModalTypes): DragRef<T> {
    const wrapElement = document.querySelector<HTMLDivElement>(`.${wrapCls}`)!;
    const rootElement = wrapElement.querySelector<HTMLDivElement>(`.ant-modal-content`)!;
    const handle =
      nzModalType === 'confirm'
        ? rootElement.querySelector<HTMLDivElement>('.ant-modal-body')!
        : rootElement.querySelector<HTMLDivElement>('.ant-modal-header')!;
    this.fixedWrapElementStyle(wrapElement);
    if (!(nzMask == null || nzMask)) {
      this.setMaxZIndex(rootElement, wrapElement);
    }
    return this.dragDrop.createDrag(handle).withHandles([handle]).withRootElement(rootElement);
  }

  /**
   * 获取随机类名
   */
  getRandomCls() {
    return NzxModalDragService.DRAG_CLS_PREFIX + Date.now() + Math.random().toString().replace('0.', '');
  }

  /**
   * 解决wrap的样式, 设置鼠标可以穿透
   * @param wrapElement
   * @protected
   */
  protected fixedWrapElementStyle(wrapElement: HTMLElement): void {
    wrapElement.style.pointerEvents = 'none';
  }

  /**
   * 当前对话框点击时,设置当前对话框z-index为最大
   * @param rootElement 当前对话框
   * @param wrapElement 待修改z-index 容器
   * @protected
   */
  protected setMaxZIndex(rootElement: HTMLElement, wrapElement: HTMLElement): void {
    rootElement.addEventListener(
      'mousedown',
      () => {
        const maxZIndex = this.getModalMaxZIndex(wrapElement);
        if (maxZIndex) {
          wrapElement.style.zIndex = maxZIndex + 1 + '';
        }
      },
      false
    );
  }

  /**
   * 获取所有对话框最大值,并确定是否需要修改
   * @param wrapElement 待修改z-index 容器
   */
  protected getModalMaxZIndex(wrapElement: HTMLElement): number | null {
    const wrapZIndex = this.getZIndex(wrapElement);
    const maxZIndex = this.modal.openModals.reduce<number>((prev, modal) => {
      // @ts-ignore
      const element = (modal.containerInstance.host || modal.containerInstance.elementRef).nativeElement;
      if (wrapElement === element) {
        return prev;
      }
      const zIndex = this.getZIndex(element);
      return zIndex > prev ? zIndex : prev;
    }, 0);
    return maxZIndex >= wrapZIndex ? maxZIndex : null;
  }

  protected getZIndex(element: HTMLElement): number {
    return +getComputedStyle(element, null).zIndex;
  }
}

import { Injectable } from '@angular/core';

import { Subject } from 'rxjs';
import { ConfirmType, ModalOptions, NzModalRef, NzModalService } from 'ng-zorro-antd/modal';
import { NzSafeAny } from 'ng-zorro-antd/core/types';
import { NzxModalDragService } from './modal-drag.service';

@Injectable()
export class NzxModalWrapService {
  constructor(public modal: NzModalService, public modalDragService: NzxModalDragService) {}

  /**
   * 创建对话框, 增加可拖拽功能
   * @param config ModalOptions
   */
  create<T, R = NzSafeAny>(config: ModalOptions<T, R>): NzModalRef<T, R> {
    return this.createModalWidthDrag(config, c => this.modal.create(c));
  }

  get openModals(): NzModalRef[] {
    return this.modal.openModals;
  }

  get afterAllClosed(): Subject<void> {
    return this.modal._afterAllClosed;
  }

  closeAll(): void {
    this.modal.closeAll();
  }

  confirm<T>(options: ModalOptions<T>, confirmType?: ConfirmType): NzModalRef<T> {
    return this.createModalWidthDrag(options, c => this.modal.confirm(c));
  }

  info<T>(options: ModalOptions<T>): NzModalRef<T> {
    return this.createModalWidthDrag(options, c => this.modal.info(c));
  }

  success<T>(options: ModalOptions<T>): NzModalRef<T> {
    return this.createModalWidthDrag(options, c => this.modal.success(c));
  }

  error<T>(options: ModalOptions<T>): NzModalRef<T> {
    return this.createModalWidthDrag(options, c => this.modal.error(c));
  }

  warning<T>(options: ModalOptions<T>): NzModalRef<T> {
    return this.createModalWidthDrag(options, c => this.modal.warning(c));
  }

  protected createModalWidthDrag<T, R = NzSafeAny>(
    config: ModalOptions<T, R>,
    create: (newConfig: ModalOptions<T, R>) => NzModalRef<T, R>
  ) {
    const wrapCls = this.modalDragService.getRandomCls();
    const newConfig = this.createModalConfig(config, wrapCls);
    const modalRef = create(newConfig);

    modalRef.afterOpen.subscribe(() => {
      const drag = this.modalDragService.createDragHandler(wrapCls, config.nzMask, newConfig.nzModalType);
      modalRef.afterClose.subscribe(() => {
        if (drag && !drag.dropped) {
          drag.dispose();
        }
      });
    });
    return modalRef;
  }

  protected createModalConfig<T, R = NzSafeAny>(config: ModalOptions<T, R>, wrapCls: string): ModalOptions<T, R> {
    const defaultConfig: ModalOptions = {
      nzMaskClosable: false,
      nzTitle: '提示'
    };
    const maskStyle = config.nzMask === false ? { nzMaskStyle: { display: 'none' } } : {};
    const newConfig = Object.assign(defaultConfig, config, maskStyle);
    newConfig.nzWrapClassName = (newConfig.nzWrapClassName || '') + ' ' + wrapCls;
    return newConfig;
  }
}

import { Injectable, Optional } from '@angular/core';
import { NzSafeAny } from 'ng-zorro-antd/core/types';

@Injectable({
  providedIn: 'root'
})
export class NzxStorageService {
  /**
   * 获取长度
   */
  get length() {
    return this.storage.length;
  }

  constructor(@Optional() public storage: Storage) {
    if (!storage) {
      this.storage = localStorage;
    }
  }

  /**
   * 清除所有数据
   */
  clear() {
    this.storage.clear();
  }

  /**
   * 获取值
   * @param key key
   */
  getItem<T = NzSafeAny>(key: string): T | null {
    const value = this.storage.getItem(key);
    return value == null ? null : (JSON.parse(value) as T);
  }

  /**
   * 获取key
   * @param index 索引
   */
  key(index: number): string | null {
    return this.storage.key(index);
  }

  /**
   * 移除值
   * @param key key
   */
  removeItem(key: string): void {
    this.storage.removeItem(key);
  }

  /**
   * 设置值
   * @param key
   * @param value
   */
  setItem<T = NzSafeAny>(key: string, value: T): void {
    this.storage.setItem(key, JSON.stringify(value));
  }
}

