import { Injectable } from '@angular/core';
import { HttpEvent, HttpHandler, HttpInterceptor, HttpRequest } from '@angular/common/http';
import { Observable, throwError, catchError } from 'rxjs';
import { HttpNotifyService } from './http-notify.service';
import { HttpError } from './http.model';
import { LogoutService } from './logout.service';
import { DEFAULT_RESPONSE_SETTING, NzxAntdService, ResponseSetting } from '@xmagic/nzx-antd';
import { NzxUtils } from '@xmagic/nzx-antd/util';

/**
 * 在 http-error-interceptor 后，进行自定义错误处理。
 */
@Injectable()
export class HttpCustomServerErrorInterceptor implements HttpInterceptor {
  protected readonly settings: Required<ResponseSetting>;

  constructor(
    protected notify: HttpNotifyService,
    protected logoutNotify: LogoutService,
    protected antdService: NzxAntdService
  ) {
    this.settings = NzxUtils.extend({}, DEFAULT_RESPONSE_SETTING, this.antdService.response) as Required<ResponseSetting>;
  }

  intercept<T>(req: HttpRequest<T>, next: HttpHandler): Observable<HttpEvent<T>> {
    return next.handle(req).pipe(catchError(error => this.handleError(error)));
  }

  /**
   * 只抛出自定义异常, 对于http内部异常由拦截器统一处理
   * @param error
   */
  handleError(error: HttpError) {
    if (error.httpError) {
      this.notify.notifyHttpOriginError(error);
      // 登录超时  强制下线
    } else if (this.settings.timeout(error) || this.settings.forceLogout(error)) {
      this.logoutNotify.notifyLogin(error);
    } else if (this.settings.defaultError(error)) {
      this.notify.notifyCustomServerError(error);
    }
    return throwError(() => error);
  }
}

import { Injectable } from '@angular/core';
import { HttpErrorResponse, HttpEvent, HttpHandler, HttpInterceptor, HttpRequest } from '@angular/common/http';
import { Observable, throwError } from 'rxjs';
import { catchError } from 'rxjs/operators';
import { HttpError } from './http.model';
import { NzSafeAny } from 'ng-zorro-antd/core/types'

/**
 * http 请求发生错误后进行处理
 */
@Injectable()
export class HttpErrorInterceptor implements HttpInterceptor {
  constructor() {}

  intercept(req: HttpRequest<NzSafeAny>, next: HttpHandler): Observable<HttpEvent<NzSafeAny>> {
    return next.handle(req).pipe(catchError(error => this.handleError(error)));
  }

  /**
   * 将错误响应包装为统一格式
   */
  handleError(errorResponse: HttpErrorResponse) {
    const { status: code, error, message } = errorResponse;
    return throwError(() => new HttpError(true, code, message, error));
  }
}

import { Injectable } from '@angular/core';
import { HttpEvent, HttpHandler, HttpInterceptor, HttpRequest } from '@angular/common/http';
import { Observable } from 'rxjs';
import { NzxAntdService } from '@xmagic/nzx-antd';
import { NzSafeAny } from 'ng-zorro-antd/core/types'

/**
 * 对请求头进行处理
 */
@Injectable()
export class HttpHeaderInterceptor implements HttpInterceptor {
  constructor(protected antdService: NzxAntdService) {}

  intercept(req: HttpRequest<NzSafeAny>, next: HttpHandler): Observable<HttpEvent<NzSafeAny>> {
    const headers: Record<string, string> = { 'X-Requested-With': 'XMLHttpRequest' };
    if (
      !req.headers.has('Content-Type') &&
      !(req.body instanceof FormData || req.body instanceof ArrayBuffer || req.body instanceof Blob)
    ) {
      headers['Content-Type'] =
        this.antdService.contentType === 'form'
          ? 'application/x-www-form-urlencoded'
          : 'application/json;charset=utf-8';
    }
    const clone = req.clone({
      setHeaders: headers
    });
    return next.handle(clone);
  }
}

import { Injectable } from '@angular/core';
import { HttpEvent, HttpHandler, HttpInterceptor, HttpRequest } from '@angular/common/http';
import { Observable, Subject } from 'rxjs';
import { filter, finalize } from 'rxjs/operators';
import { HttpLoadingService } from './http-loading.service';
import { LOADING_ENABLED } from '@xmagic/nzx-antd/service';
import { NzSafeAny } from 'ng-zorro-antd/core/types';

/**
 * http 请求发送时，调用 loading service 显示加载中图标或者做一些其它处理。
 */
@Injectable()
export class HttpLoadingInterceptor implements HttpInterceptor {
  private showStatusChange = new Subject<boolean>();
  private requestCount = 0;

  constructor(private loadingService: HttpLoadingService) {
    this.loadingService.init(this.showStatusChange.pipe(filter(() => this.requestCount === 0)));
  }

  intercept(req: HttpRequest<NzSafeAny>, next: HttpHandler): Observable<HttpEvent<NzSafeAny>> {
    const show = req.context.get(LOADING_ENABLED);
    if (show) {
      this.show();
    }
    return next.handle(req).pipe(finalize(() => show && this.hide()));
  }

  /**
   * 显示loading
   */
  show() {
    this.showStatusChange.next(true);
    this.requestCount++;
  }

  /**
   * 隐藏loading
   */
  hide() {
    this.requestCount--;
    this.showStatusChange.next(false);
  }
}

import { Injectable } from '@angular/core';
import { HttpEvent, HttpHandler, HttpInterceptor, HttpParams, HttpRequest } from '@angular/common/http';
import { Observable } from 'rxjs';
import { HttpDefaultCodec } from './http-default-encoder';
import { NzSafeAny } from 'ng-zorro-antd/core/types';
import { NzxUtils } from '@xmagic/nzx-antd/util';

/**
 * 请求参数处理
 */
@Injectable()
export class HttpParamsInterceptor implements HttpInterceptor {
  constructor() {}

  intercept(req: HttpRequest<NzSafeAny>, next: HttpHandler): Observable<HttpEvent<NzSafeAny>> {
    return next.handle(this.processParameters(req));
  }

  processParameters(req: HttpRequest<NzSafeAny>) {
    if (req.params == null) {
      return req;
    }
    return req.clone({
      params: this.processGetParams(req.params)
    });
  }

  processGetParams(params: HttpParams) {
    const result: Record<string, NzSafeAny> = { r: Math.random() };

    params.keys().reduce((acc, key) => {
      const values = params.getAll(key);
      if (values) {
        acc[key] = values.length > 1 ? values : values[0];
        return acc;
      }
      return acc;
    }, result);

    return new HttpParams({ fromString: NzxUtils.serialize(result), encoder: HttpDefaultCodec });
  }
}

import { Injectable } from '@angular/core';
import {
  HttpEvent,
  HttpEventType,
  HttpHandler,
  HttpInterceptor,
  HttpRequest,
  HttpResponse
} from '@angular/common/http';
import { Observable, Subscriber } from 'rxjs';
import { switchMap } from 'rxjs/operators';
import { HttpError, ResponseModel } from './http.model';
import { DEFAULT_RESPONSE_SETTING, NzxAntdService, ResponseSetting } from '@xmagic/nzx-antd';
import { NzxUtils } from '@xmagic/nzx-antd/util';

/**
 * 处理服务器返回的数据，改变其结构。
 */
@Injectable()
export class HttpResponseParseInterceptor implements HttpInterceptor {
  protected readonly settings: Required<ResponseSetting>;
  constructor(protected antdService: NzxAntdService) {
    this.settings = NzxUtils.extend({}, DEFAULT_RESPONSE_SETTING, this.antdService.response) as Required<ResponseSetting>;
  }

  intercept<T>(req: HttpRequest<T>, next: HttpHandler): Observable<HttpEvent<T>> {
    return next.handle(req).pipe(
      switchMap(response => {
        return new Observable<HttpEvent<T>>(observ => {
          if (response.type !== HttpEventType.Response) {
            observ.next(response);
            return;
          }
          this.processData<T>(observ, req, response);
        });
      })
    );
  }

  protected processData<T>(
    subscriber: Subscriber<HttpEvent<T>>,
    req: HttpRequest<T>,
    response: HttpResponse<ResponseModel>
  ) {

    const contentType = (response.headers.get('content-type') || '').toLowerCase();

    const isJsonResponse = contentType.indexOf('application/json') !== -1;
    if (!isJsonResponse) {
      subscriber.next(response as HttpResponse<T>);
      subscriber.complete();
      return;
    }

    const { data: dataProp, code: codeProp, message: messageProp, success: successProp } = this.settings;

    // 处理下载文件错误
    if (isJsonResponse && response.body instanceof Blob) {
      const reader = new FileReader();
      reader.onload = () => {
        const err = JSON.parse(reader.result as string) as ResponseModel<T>;
        const httpError = new HttpError(false, NzxUtils.get(err, codeProp, 0), NzxUtils.get(err, messageProp), err);
        subscriber.error(httpError);
      };
      reader.readAsText(response.body);
      return;
    }

    const body = response.body || {};
    if (successProp(response)) {
      const resp = response.clone({ body: dataProp ? NzxUtils.get(body, dataProp) : body });
      subscriber.next(resp);
      subscriber.complete();
    } else {
      const httpError = new HttpError(false, NzxUtils.get(body, codeProp, 0), NzxUtils.get(body, messageProp), body);
      subscriber.error(httpError);
    }
  }
}

import { Injectable } from '@angular/core';
import { HttpEvent, HttpHandler, HttpInterceptor, HttpRequest } from '@angular/common/http';
import { Observable } from 'rxjs';
import { NzSafeAny } from 'ng-zorro-antd/core/types';
import { HttpRequestOptions, NzxAntdService } from '@xmagic/nzx-antd';

/**
 * 处理 URL
 */
@Injectable()
export class HttpUrlInterceptor implements HttpInterceptor {
  constructor(protected antdService: NzxAntdService) {}

  intercept(req: HttpRequest<NzSafeAny>, next: HttpHandler): Observable<HttpEvent<NzSafeAny>> {
    return next.handle(this.processUrl(req));
  }

  processUrl(req: HttpRequest<NzSafeAny>): HttpRequest<NzSafeAny> {
    let url = req.url;
    if (!/^http/i.test(url)) {
      if (!/^\//.test(url)) {
        url = '/' + url;
      }
      url = (this.antdService.basePath || '') + url;
    }

    // 使用自定义请求处理器
    if (this.antdService.handleRequest) {
      const newReq = this.antdService.handleRequest(req, url);
      if (newReq) {
        if ((newReq as HttpRequest<NzSafeAny>).clone) {
          return newReq as HttpRequest<NzSafeAny>;
        }
        const option = newReq as HttpRequestOptions;
        if (!option.url) {
          option.url = url;
        }
        return req.clone(option);
      }
    }
    return req.clone({ url });
  }
}

