import { Directive, EmbeddedViewRef, Input, OnDestroy, TemplateRef, ViewContainerRef } from '@angular/core';
import { of, Subscription } from 'rxjs';
import { NzxAntdService } from '@xmagic/nzx-antd';
import { NzSafeAny } from 'ng-zorro-antd/core/types'

/**
 * 权限指令, 有权限渲染模板,否则渲染else模板
 * @example
 * <button *auth="['code1', 'code2']; else notAuth">有权限时显示</button>
 * <button *auth="'code1'; else notAuth">有权限时显示</button>
 * <ng-template #notAuth>没有权限时显示</ng-template>
 */
@Directive({
  selector: '[auth]'
})
export class AuthDirective<T = unknown> implements OnDestroy {
  /** @internal */
  static authUseIfTypeGuard: void;
  static ngTemplateGuard_auth: 'binding';
  private readonly changeSubscription?: Subscription;
  private authSubscription?: Subscription;

  protected _context: AuthContext<T> = new AuthContext<T>();
  protected _thenTemplateRef: TemplateRef<AuthContext<T>> | null = null;
  protected _elseTemplateRef: TemplateRef<AuthContext<T>> | null = null;
  protected _thenViewRef: EmbeddedViewRef<AuthContext<T>> | null = null;
  protected _elseViewRef: EmbeddedViewRef<AuthContext<T>> | null = null;

  /**
   *
   * 为 `auth` 将要渲染的模板确保正确的上下文类型。
   * 该方法用于向 Ivy 模板类型检查编译器发出信号，即 `auth` 结构化指令会使用特定的上下文类型渲染其模板。
   *
   */
  static ngTemplateContextGuard<T>(
    dir: AuthDirective<T>,
    ctx: NzSafeAny
  ): ctx is AuthContext<Exclude<T, false | 0 | '' | null | undefined>> {
    return true;
  }

  protected hasAuth: Required<NzxAntdService>['hasAuth'] = () => of(true);

  constructor(
    protected _viewContainer: ViewContainerRef,
    public templateRef: TemplateRef<AuthContext<T>>,
    public antdService: NzxAntdService
  ) {
    this._thenTemplateRef = templateRef;

    if (this.antdService.authChange) {
      this.changeSubscription = this.antdService.authChange().subscribe(() => this._updateView());
    }

    if (this.antdService.hasAuth) {
      this.hasAuth = this.antdService.hasAuth;
    }
  }

  /**
   * 布尔表达式，将其作为显示模板的条件进行计算。
   *
   */
  @Input()
  set auth(condition: T) {
    this._context.$implicit = this._context.auth = condition;
    this._updateView();
  }

  /**
   * 当此条件表达式计算为 true 时要显示的模板。
   */
  @Input()
  set authThen(templateRef: TemplateRef<AuthContext<T>> | null) {
    assertTemplate('authThen', templateRef);
    this._thenTemplateRef = templateRef;
    this._thenViewRef = null; // clear previous view if any.
    this._updateView();
  }

  /**
   * 当此条件表达式计算为 false 时要显示的模板。
   */
  @Input()
  set authElse(templateRef: TemplateRef<AuthContext<T>> | null) {
    assertTemplate('authElse', templateRef);
    this._elseTemplateRef = templateRef;
    this._elseViewRef = null; // clear previous view if any.
    this._updateView();
  }

  protected _updateView() {
    if (this.authSubscription) {
      this.authSubscription.unsubscribe();
      this.authSubscription = undefined;
    }
    this.authSubscription = this.hasAuth(this._context.$implicit).subscribe(auth => {
      if (this.canRender(auth, this._context.$implicit)) {
        if (!this._thenViewRef) {
          this._viewContainer.clear();
          this._elseViewRef = null;
          if (this._thenTemplateRef) {
            this._thenViewRef = this._viewContainer.createEmbeddedView(this._thenTemplateRef, this._context);
          }
        }
      } else {
        if (!this._elseViewRef) {
          this._viewContainer.clear();
          this._thenViewRef = null;
          if (this._elseTemplateRef) {
            this._elseViewRef = this._viewContainer.createEmbeddedView(this._elseTemplateRef, this._context);
          }
        }
      }
    });
  }

  /**
   * 是否允许渲染
   * @param hasAuth 是否有权限
   * @param value 权限码
   * @protected
   */
  protected canRender(hasAuth: boolean, value: T) {
    return hasAuth;
  }

  ngOnDestroy(): void {
    if (this.changeSubscription) {
      this.changeSubscription.unsubscribe();
    }
    if (this.authSubscription) {
      this.authSubscription.unsubscribe();
    }
  }
}

/**
 * @publicApi
 */
export class AuthContext<T = unknown> {
  public $implicit: T = null!;
  public auth: T = null!;
}

function assertTemplate(property: string, templateRef: TemplateRef<NzSafeAny> | null): void {
  const isTemplateRefOrNull = !!(!templateRef || templateRef.createEmbeddedView);
  if (!isTemplateRefOrNull) {
    throw new Error(`${property} must be a TemplateRef, but received '${templateRef}'.`);
  }
}

export abstract class BaseControl<T> {
  protected nzxDisabled?: boolean;
  onChange: (value: T) => void = () => null;
  onTouched: () => void = () => null;

  registerOnChange(fn: (_: T) => void): void {
    this.onChange = fn;
  }

  registerOnTouched(fn: () => void): void {
    this.onTouched = fn;
  }

  setDisabledState?(isDisabled: boolean): void {
    this.nzxDisabled = isDisabled;
  }
}

import { AbstractControl, FormArray, FormControl, FormGroup, ValidationErrors, ValidatorFn } from '@angular/forms';
import { NzSafeAny } from 'ng-zorro-antd/core/types';
import { NzxUtils } from './utils';

/**
 * 表单工具
 */
export class FormUtilsClass {
  /**
   * 遍历表单控件
   * @param form 表单或者表单元素
   * @param callback 回调函数
   */
  forEachFormControl(form: AbstractControl, callback: (ctrl: AbstractControl) => void) {
    if (form instanceof FormGroup) {
      callback(form);
      for (const i in form.controls) {
        const ctrl = form.controls[i];
        if (ctrl instanceof FormControl) {
          callback(ctrl);
        } else {
          this.forEachFormControl(ctrl, callback);
        }
      }
      return;
    }

    if (form instanceof FormArray) {
      callback(form);
      for (let i = 0; i < form.length; i++) {
        const ctrl = form.at(i);
        if (ctrl instanceof FormControl) {
          callback(ctrl);
        } else {
          this.forEachFormControl(ctrl, callback);
        }
      }
      return;
    }

    if (form instanceof FormControl) {
      callback(form);
      return;
    }
  }

  /**
   * 执行验证
   * @param form 表单
   * @param opts 配置
   */
  validate(
    form: AbstractControl,
    opts: {
      onlySelf?: boolean;
      emitEvent?: boolean;
      updateValueAndValidity?: boolean;
    } = { updateValueAndValidity: false }
  ): boolean {
    this.forEachFormControl(form, ctrl => {
      ctrl.markAsDirty(opts);
      opts.updateValueAndValidity && ctrl.updateValueAndValidity(opts);
    });
    return form.valid;
  }

  /**
   * 清空错误信息
   */
  clearError(form: AbstractControl): void {
    this.forEachFormControl(form, ctrl => ctrl.setErrors(null));
  }

  /**
   * 获取错误信息
   * @param control
   */
  getMessage(control: AbstractControl): string | null {
    if (!control.errors) {
      return null;
    }

    for (const key in control.errors) {
      return control.errors[key]?.message || '输入不正确';
    }
    return null;
  }

  /**
   * 必填验证
   * @param message 错误信息
   * @param hasValue 是否有值函数判断
   */
  required<T = NzSafeAny>(message: string = '不能为空', hasValue: (value: T) => boolean = value => !!value): ValidatorFn {
    return (control: AbstractControl) => {
      return hasValue(control.value) ? null : { required: { message, value: control.value } };
    };
  }

  /**
   * 根据指定函数验证
   * @param operator 操作函数
   * @param errMessage 错误信息
   */
  singleValidator<T>(operator: (value: T) => boolean, errMessage: ValidationErrors): ValidatorFn {
    return (control: AbstractControl) => {
      if (control.value == null || control.value.length === 0) {
        return null;
      }
      return operator(control.value) ? null : errMessage;
    };
  }

  /**
   * 根据正则表达式验证
   * @param exp 正则表达式
   * @param message 错误信息
   */
  pattern(exp: RegExp | string, message: string): ValidatorFn {
    const reg = NzxUtils.isString(exp) ? new RegExp(exp, 'mig') : exp;
    return this.singleValidator<string>(v => reg.test(v), { pattern: { message } });
  }

  /**
   * 小于验证
   * @param num 数字
   * @param message 错误信息
   */
  lt(num: number, message: string): ValidatorFn {
    return this.singleValidator<string>(v => Number(v) < num, { lt: { message } });
  }

  /**
   * 小于等于验证
   * @param num 数字
   * @param message 错误信息
   */
  lte(num: number, message: string): ValidatorFn {
    return this.singleValidator<string>(v => Number(v) <= num, { lte: { message } });
  }

  /**
   * 大于验证
   * @param num 数字
   * @param message 错误信息
   */
  gt(num: number, message: string): ValidatorFn {
    return this.singleValidator<string>(v => Number(v) > num, { gt: { message } });
  }

  /**
   * 大于等于验证
   * @param num 数字
   * @param message 错误信息
   */
  gte(num: number, message: string): ValidatorFn {
    return this.singleValidator<string>(v => Number(v) >= num, { gte: { message } });
  }

  /**
   * 包含指定字符串验证
   * @param str 字符串
   * @param ignore 忽略大小写
   * @param message 错误信息
   */
  contains(str: string, ignore: boolean, message: string): ValidatorFn {
    const newStr = ignore ? str.toLowerCase() : str;
    return this.singleValidator<string>(v => (ignore ? v.toLowerCase() : v).indexOf(newStr) !== -1, {
      contains: { message }
    });
  }

  /**
   * min <= x <= max 验证
   * @param min 最小值
   * @param max 最大值
   * @param message 错误信息
   */
  between(min: number, max: number, message: string): ValidatorFn {
    return this.singleValidator<string>(
      value => {
        const v = Number(value);
        return v >= min && v <= max;
      },
      { between: { message } }
    );
  }

  /**
   * 数字校验
   * @param minus 是否允许负数
   * @param fraction 保留小数有效数字位数
   * @param message 错误提示信息
   */
  number(minus?: boolean, fraction?: number, message: string = '数字格式错误'): ValidatorFn {
    return this.singleValidator<string>(value => NzxUtils.isNum(value, minus, fraction), { number: { message } });
  }

  /**
   * 校验手机号（中国）
   * @param message 错误提示信息
   */
  mobile(message: string = '手机号格式错误'): ValidatorFn {
    return this.singleValidator<string>(value => NzxUtils.isMobile(value), { mobile: { message } });
  }

  /**
   * 校验邮箱
   * @param message 错误提示信息
   */
  email(message: string = '邮箱格式错误'): ValidatorFn {
    return this.singleValidator<string>(value => NzxUtils.isEmail(value), { email: { message } });
  }

  /**
   * 校验ip
   * @param message 错误提示信息
   */
  ip(message: string = 'IP格式错误'): ValidatorFn {
    return this.singleValidator<string>(value => NzxUtils.isIp(value), { ip: { message } });
  }

  /**
   * 校验url,支持ip v4, ip v6
   * @param message 错误提示信息
   */
  url(message: string = 'URL格式错误'): ValidatorFn {
    return this.singleValidator<string>(value => NzxUtils.isUrl(value), { url: { message } });
  }

  /**
   * 校验中文
   * @param message 错误提示信息
   */
  chinese(message: string = '只允许输入中文字符'): ValidatorFn {
    return this.singleValidator<string>(value => NzxUtils.isChinese(value), { chinese: { message } });
  }

  /**
   * 验证两个字段
   * @param form 表单
   * @param otherField 另一个被验证的字段名称
   * @param compare 比较器
   * @param errMessage  错误提示
   */
  twoControl(
    form: FormGroup | FormArray | (() => FormGroup | FormArray),
    otherField: Array<string | number> | string,
    compare: (v1: NzSafeAny, v2: NzSafeAny) => boolean,
    errMessage: ValidationErrors
  ): ValidatorFn {
    const fn = NzxUtils.isFunction(form) ? form : () => form;
    return (control: AbstractControl): ValidationErrors | null => {
      if (!control?.value) {
        return null;
      }

      const other = fn().get(otherField);
      if (!other?.value) {
        return null;
      }

      if (compare(other?.value, control?.value)) {
        if (!other.valid) {
          other.updateValueAndValidity();
        }
        return null;
      }
      return errMessage;
    };
  }

  /**
   * 两个字段相等验证
   * @param form 表单
   * @param otherField 另一个被验证的字段名
   * @param message 提示信息
   */
  equalControl(
    form: FormGroup | FormArray | (() => FormGroup | FormArray),
    otherField: Array<string | number> | string,
    message: string
  ): ValidatorFn {
    const errMessage = { equalValidator: { message } };
    return this.twoControl(form, otherField, (v1, v2) => v1 === v2, errMessage);
  }

  /**
   * 大于另一个字段验证
   * @param form 表单
   * @param otherField 另一个被验证的字段名
   * @param message 提示信息
   */
  gtControl(form: FormGroup | FormArray, otherField: Array<string | number> | string, message: string): ValidatorFn {
    const errMessage = { gtValidator: { message } };
    return this.twoControl(form, otherField, (v1, v2) => Number(v1) > Number(v2), errMessage);
  }

  /**
   * 大于等于另一个字段验证
   * @param form 表单
   * @param otherField 另一个被验证的字段名
   * @param message 提示信息
   */
  gteControl(
    form: FormGroup | FormArray | (() => FormGroup | FormArray),
    otherField: Array<string | number> | string,
    message: string
  ): ValidatorFn {
    const errMessage = { gteValidator: { message } };
    return this.twoControl(form, otherField, (v1, v2) => Number(v1) >= Number(v2), errMessage);
  }

  /**
   * 小于于另一个字段验证
   * @param form 表单
   * @param otherField 另一个被验证的字段名
   * @param message 提示信息
   */
  ltControl(
    form: FormGroup | FormArray | (() => FormGroup | FormArray),
    otherField: Array<string | number> | string,
    message: string
  ): ValidatorFn {
    const errMessage = { ltValidator: { message } };
    return this.twoControl(form, otherField, (v1, v2) => Number(v1) < Number(v2), errMessage);
  }

  /**
   * 小于等于另一个字段验证
   * @param form 表单
   * @param otherField 另一个被验证的字段名
   * @param message 提示信息
   */
  lteControl(
    form: FormGroup | FormArray | (() => FormGroup | FormArray),
    otherField: Array<string | number> | string,
    message: string
  ): ValidatorFn {
    const errMessage = { lteValidator: { message } };
    return this.twoControl(form, otherField, (v1, v2) => Number(v1) <= Number(v2), errMessage);
  }

  /**
   * 唯一验证
   * @param form
   * @param message
   */
  unique(form: FormGroup | FormArray, message: string = '不允许重复'): ValidatorFn {
    return (control: AbstractControl): ValidationErrors | null => {
      if (!control.value) {
        return null;
      }
      for (const c in form.controls) {
        const ctl = form.get(c);
        if (control === ctl) {
          continue;
        }
        if (control.value === ctl?.value) {
          return { uniqueValidator: { message } };
        }
      }
      return null;
    };
  }
}

export const NzxFormUtils = new FormUtilsClass();

import { NzSafeAny } from 'ng-zorro-antd/core/types'

/**
 * 服务端响应实体, 可以通过设置 http请求参数
 * {observe: 'response'} 拿到完整数据
 */
export interface ResponseModel<T = NzSafeAny> {
  /**
   * 服务端返回编码
   */
  code?: number;
  message?: string;
  data?: T;
}

/**
 * http 请求出错后，在中间件中封闭为统一的格式。
 */
export class HttpError<T = NzSafeAny> {
  /**
   *
   * @param httpError 是否是HTTP原始异常
   * @param code 错误码, 如果是HTTP原始异常,则为status code
   * @param message 错误消息
   * @param body 返回数据
   */
  constructor(public httpError: boolean, public code: number, public message: string, public body: T) {}
}

import { Directive, Inject, Input, TemplateRef, ViewContainerRef } from '@angular/core';
import { NzSafeAny } from 'ng-zorro-antd/core/types';

/**
 * 允许在模板内复用计算值（包含异步），避免重复重新计算。
 * @example
 *
 * <div *let="value1 as v">
 *   <p>{{ v }}</p>
 * </div>
 * <div *let="time$ | async as time">
 *   <p>{{ time }}</p>
 * </div>
 */
@Directive({ selector: '[let]' })
export class LetDirective<T> {
  @Input() let!: T;

  constructor(@Inject(ViewContainerRef) vc: ViewContainerRef, @Inject(TemplateRef) ref: TemplateRef<LetContext<T>>) {
    vc.createEmbeddedView(ref, new LetContext<T>(this));
  }

  static ngTemplateContextGuard<T>(_dir: LetDirective<T>, _ctx: NzSafeAny): _ctx is LetDirective<T> {
    return true;
  }
}

export class LetContext<T> {
  constructor(private readonly directive: LetDirective<T>) {}

  get $implicit(): T {
    return this.directive.let;
  }

  get let(): T {
    return this.directive.let;
  }
}

import { ElementRef } from '@angular/core';
import { isNil } from 'ng-zorro-antd/core/util';
import { Observable } from 'rxjs';
import { NzSafeAny } from 'ng-zorro-antd/core/types';

const toStr = Object.prototype.toString;
const hasOwn = Object.prototype.hasOwnProperty;
const defineProperty = Object.defineProperty;
const gOPD = Object.getOwnPropertyDescriptor;

class UtilsClass {
  assign = this.extend;

  defaultIfy<T = NzSafeAny>(obj: T, defaultValue: NzSafeAny) {
    return isNil(obj) ? defaultValue : obj;
  }

  /**
   * 根据属性路径获取对象的属性值
   * @param obj 原始对象
   * @param path 属性路径
   * @param defaultValue 当属性不存在或为undefined返回defaultValue
   */
  get(obj: NzSafeAny, path?: string | string[], defaultValue?: NzSafeAny) {
    if (!obj || path == null || path.length === 0) {
      return defaultValue;
    }
    if (!Array.isArray(path)) {
      path = path.indexOf('.') ? path.split('.') : [path];
    }
    if (path.length === 1) {
      const checkObj = obj[path[0]];
      return typeof checkObj === 'undefined' ? defaultValue : checkObj;
    }
    return path.reduce((o, k) => (o || {})[k], obj) || defaultValue;
  }

  trim(string: string) {
    return (string || '').replace(/^[\s\uFEFF]+|[\s\uFEFF]+$/g, '');
  }

  /**
   * 获取唯一Id号
   */
  getUUID() {
    return Date.now() + Math.random().toString().replace('0.', '');
  }

  /**
   * 深度clone
   * @param target
   */
  clone<T = NzSafeAny>(target: T): T {
    const _target = NzxUtils.isArray(target) ? [] : {};
    return this.extend(_target as T, target);
  }

  // If name is '__proto__', and Object.defineProperty is available, define __proto__ as an own property on target
  private setProperty<T = NzSafeAny>(target: T, options: { name: string; newValue: NzSafeAny }) {
    if (defineProperty && options.name === '__proto__') {
      defineProperty(target, options.name, {
        enumerable: true,
        configurable: true,
        value: options.newValue,
        writable: true
      });
    } else {
      // @ts-ignore
      target[options.name] = options.newValue;
    }
  }

  // Return undefined instead of __proto__ if '__proto__' is not an own property
  private getProperty<T = NzSafeAny>(obj: T, name: string) {
    if (name === '__proto__') {
      if (!hasOwn.call(obj, name)) {
        return void 0;
      } else if (gOPD) {
        // @ts-ignore
        return gOPD(obj, name).value;
      }
    }
    // @ts-ignore
    return obj[name];
  }

  /**
   * 继承
   * @param target
   * @param args
   */
  extend<T = NzSafeAny>(target: T, ...args: NzSafeAny[]): T {
    let copyIsArray, clone;

    for (const options of args) {
      if (options == null) {
        continue;
      }

      for (const name in options) {
        const src = this.getProperty(target, name);
        const copy = this.getProperty(options, name);
        if (target === copy || copy === undefined) {
          continue;
        }

        // tslint:disable-next-line:no-conditional-assignment
        if (NzxUtils.isPlainObject(copy) || (copyIsArray = NzxUtils.isArray(copy))) {
          if (copyIsArray) {
            copyIsArray = false;
            clone = src && NzxUtils.isArray(src) ? src : [];
          } else {
            clone = src && NzxUtils.isPlainObject(src) ? src : {};
          }

          this.setProperty(target, { name, newValue: this.extend(clone, copy) });
        } else {
          this.setProperty(target, { name, newValue: copy });
        }
      }
    }

    return target;
  }

  /**
   * 根据开始和结束数字返回一个数组
   * @param start 起始数字
   * @param end 结束数字
   * @param fill 填充内容,默认
   */
  range<T = NzSafeAny>(start: number, end: number, fill?: ((num: number, index: number) => T) | T): T[] {
    const list: T[] = [];
    const fn = fill == null ? (i: number) => i as unknown as T : this.isFunction(fill) ? fill : () => fill;
    let index = 0;
    for (let i = start; i < end; i++) {
      list.push(fn(i, index++));
    }
    return list;
  }

  /**
   * 遍历树结构
   * @param treeNodes TreeNode[]
   * @param accept 函数返回false即结束遍历, true或者undefined将继续遍历
   * @param childrenName 子节点字段名称
   */
  forEachTree<T extends TreeNode>(
    treeNodes: T[],
    accept: (node: T, parentNode: T | undefined, level: number) => boolean | void,
    childrenName: keyof T | TreeChildren<T> = 'children'
  ): void {
    if (!treeNodes || !treeNodes.length) {
      return;
    }

    const getChildren =
      typeof childrenName === 'string' ? (node: T) => node[childrenName] : (childrenName as TreeChildren<T>);

    let stack: T[] = [];
    const parents: T[] = [];
    let level = 0;
    let count = 0;

    for (const t of treeNodes) {
      stack = [t];
      parents.length = 0;
      level = 0;
      count = 0;

      while (stack.length) {
        const item = stack.shift()!;
        if (item == null) {
          continue;
        }

        const parent = parents[level - 1];
        if (accept(item, parent, level) === false) {
          return;
        }

        const children = getChildren(item, parent, level);
        if (children && children.length) {
          stack = stack.concat(children);
          parents.push(item);
          count = children.length;
          level++;
        } else if (!--count) {
          level--;
        }
      }
    }
  }

  /**
   * 过滤树, 生成新的树结构
   * @param treeNodes 树结构
   * @param predicate 过滤函数
   * @param childrenName 子节点名称,默认 'children'
   */
  filterTree<T extends TreeNode>(
    treeNodes: T[],
    predicate: (node: T) => boolean,
    childrenName: keyof T = 'children'
  ): T[] {
    const filter = (list: T[], children: T[]) => {
      for (const node of list) {
        const newNode: T = { ...node, [childrenName]: [] };
        if (node[childrenName]?.length) {
          filter(node[childrenName], newNode[childrenName]);
        }

        if (predicate(newNode)) {
          children.push(newNode);
        }
      }
    };
    const result: T[] = [];
    filter(treeNodes, result);
    return result;
  }

  /**
   * 列表转树结构
   * @param list 列表数据
   * @param idName id的属性名
   * @param pidName parentId属性名
   * @param childrenName children属性名
   */
  listToTree<T extends { pid?: string; id?: string; [key: string]: NzSafeAny }>(
    list: T[],
    idName: keyof T = 'id',
    pidName: keyof T = 'pid',
    childrenName: string = 'children'
  ): T[] {
    if (!list || !list.length) {
      return [];
    }
    const nodeMap: { [key: string]: NzSafeAny } = {};
    for (const node of list) {
      nodeMap[node[idName]] = node;
      // @ts-ignore
      node[childrenName] = [];
    }

    const treeNodes: T[] = [];
    for (const node of list) {
      const pid = node[pidName];
      const parent = nodeMap[node[pidName]];
      if (pid && parent) {
        parent[childrenName].push(node);
      } else {
        treeNodes.push(node);
      }
    }
    return treeNodes;
  }

  /**
   * 获取同步请求的响应结果
   * @param observable
   * @example
   * const myObservable = this.http.get<string>('url', { context: new HttpContext().set(SYNCED_ENABLED, true)});
   * const value = getAjaxValue(myObservable); // value为string类型
   */
  getAjaxValue<T = NzSafeAny>(observable: Observable<T>) {
    let value!: T;
    observable.subscribe(result => (value = result)).unsubscribe();
    return value;
  }

  /**
   * 根据不同的参数类型 获取dom元素
   * @param target
   */
  getElement(target: ElementRef | HTMLElement | string): HTMLElement | null {
    if (!target) {
      return null;
    }

    if (target instanceof HTMLElement) {
      return target;
    }

    const eleRef = target as ElementRef;
    if (eleRef.nativeElement) {
      return eleRef.nativeElement;
    }

    if (typeof target === 'string') {
      return document.querySelector(target);
    }
    return null;
  }

  /**
   * 字符串模版函数
   * @param template 模版
   * @param data 数据
   * @return 返回渲染后的Html
   * @example
   * Utils.format('{s.0.name}', { s: [{name: 111}] }) => 111
   */
  format(template: string | null, data?: Record<string, NzSafeAny>): string {
    if (template == null || !data) {
      return template || '';
    }
    return template.replace(/\{([\w\.]*)\}/g, (str, key) => {
      const path = key.split('.');
      let v = data[path.shift()];
      for (let i = 0, size = path.length; i < size && v !== null; i++) {
        v = v[path[i]];
      }
      return v == null ? '' : v.toString();
    });
  }

  /**
   * 对Date的扩展，将 Date 转化为指定格式的String
   * 月(M)、日(d)、12小时(h)、24小时(H)、分(m)、秒(s)、周(E)、季度(q) 可以用 1-2 个占位符
   * 年(y)可以用 1-4 个占位符，毫秒(S)只能用 1 个占位符(是 1-3 位的数字)
   * eg:
   * NeKit.formatDate(new Date(), 'yyyy-MM-dd hh:mm:ss.S') ==> 2006-07-02 08:09:04.423
   * NeKit.formatDate(new Date(), 'yyyy-MM-dd 星期E HH:mm:ss') ==> 2009-03-10 星期二 20:09:04
   * NeKit.formatDate(new Date(), 'yyyy-MM-dd 星期e hh:mm:ss') ==> 2009-03-10 星期2 08:09:04
   * NeKit.formatDate(new Date(), 'yyyy-MM-dd 第w周 hh:mm:ss') ==> 2009-03-10 第2周 08:09:04
   * NeKit.formatDate(new Date(), 'yyyy-M-d h:m:s.S') ==> 2006-7-2 8:9:4.18
   */
  formatDate(srcDate: Date, fmt: string) {
    if (!srcDate) {
      return '';
    }
    const week = {
      0: '\u65e5',
      1: '\u4e00',
      2: '\u4e8c',
      3: '\u4e09',
      4: '\u56db',
      5: '\u4e94',
      6: '\u516d'
    };

    const o = {
      'M+': srcDate.getMonth() + 1,
      'd+': srcDate.getDate(),
      'h+': srcDate.getHours() % 12 === 0 ? 12 : srcDate.getHours() % 12,
      'H+': srcDate.getHours(),
      'm+': srcDate.getMinutes(),
      's+': srcDate.getSeconds(),
      'q+': Math.floor((srcDate.getMonth() + 3) / 3),
      'S+': srcDate.getMilliseconds(),
      'e+': srcDate.getDay(),
      // @ts-ignore
      E: week[srcDate.getDay()],
      'w+': (date => {
        const date2 = new Date(date.getFullYear(), 0, 1);
        const day = (date2.getDay() ? date2.getDay() : 7) - (date.getDay() ? date.getDay() : 7);
        return Math.ceil(Math.round((date.getTime() - date2.getTime() + day * (24 * 3600000)) / 86400000) / 7) + 1;
      })(srcDate)
    };
    if (/(y+)/.test(fmt)) {
      fmt = fmt.replace(RegExp.$1, (srcDate.getFullYear() + '').substr(4 - RegExp.$1.length));
    }
    for (const k in o) {
      if (new RegExp('(' + k + ')').test(fmt)) {
        // @ts-ignore
        fmt = fmt.replace(RegExp.$1, RegExp.$1.length === 1 ? o[k] : ('00' + o[k]).substr(('' + o[k]).length));
      }
    }
    return fmt;
  }

  is(val: unknown, type: string) {
    return toStr.call(val) === `[object ${type}]`;
  }

  isDef<T = unknown>(val?: T): val is T {
    return typeof val !== 'undefined';
  }

  isUnDef<T = unknown>(val?: T): val is T {
    return !this.isDef(val);
  }

  isObject(val: NzSafeAny): val is Record<NzSafeAny, NzSafeAny> {
    return val !== null && this.is(val, 'Object');
  }

  isEmpty<T = unknown>(val: T): val is T {
    // @ts-ignore
    return val == null || val.length === 0;
  }

  isDate(val: unknown): val is Date {
    return this.is(val, 'Date');
  }

  isNull(val: unknown): val is null {
    return val === null;
  }

  isNil(val: unknown): val is null | undefined {
    return this.isUnDef(val) && this.isNull(val);
  }

  isNotNil(val: unknown): val is null | undefined {
    return !this.isNil(val);
  }

  isNumber(val: unknown): val is number {
    return this.is(val, 'Number');
  }

  isPromise<T = NzSafeAny>(val: unknown): val is Promise<T> {
    return this.is(val, 'Promise') || (this.isObject(val) && this.isFunction(val.then) && this.isFunction(val.catch));
  }

  isObservable(obj: NzSafeAny): obj is Observable<NzSafeAny> {
    return obj instanceof Observable || (obj && typeof obj.subscribe === 'function');
  }

  isString(val: unknown): val is string {
    return this.is(val, 'String');
  }

  isFunction(val: unknown): val is Function {
    return typeof val === 'function';
  }

  isBoolean(val: unknown): val is boolean {
    return this.is(val, 'Boolean');
  }

  isRegExp(val: unknown): val is RegExp {
    return this.is(val, 'RegExp');
  }

  isArray(val: NzSafeAny): val is Array<NzSafeAny> {
    if (typeof Array.isArray === 'function') {
      return Array.isArray(val);
    }
    return toStr.call(val) === '[object Array]';
  }

  isWindow(val: NzSafeAny): val is Window {
    return typeof window !== 'undefined' && this.is(val, 'Window');
  }

  isElement(val: unknown): val is Element {
    return this.isObject(val) && !!val.tagName;
  }

  isMap(val: unknown): val is Map<NzSafeAny, NzSafeAny> {
    return this.is(val, 'Map');
  }

  /**
   * 是否为手机号（中国）
   * @param value
   */
  isMobile(value: string): boolean {
    return /^(0|\\+?86|17951)?1[0-9]{10}$/.test(value);
  }

  /**
   * 是否IP4地址（支持v4、v6）
   * @param ip
   */
  isIp(ip: string) {
    return /^(?:^(?:25[0-5]|2[0-4]\\d|1\\d\\d|[1-9]\\d|\\d)(?:\\.(?:25[0-5]|2[0-4]\\d|1\\d\\d|[1-9]\\d|\\d)){3}$)|(?:^(?:(?:[a-fA-F\\d]{1,4}:){7}(?:[a-fA-F\\d]{1,4}|:)|(?:[a-fA-F\\d]{1,4}:){6}(?:(?:25[0-5]|2[0-4]\\d|1\\d\\d|[1-9]\\d|\\d)(?:\\.(?:25[0-5]|2[0-4]\\d|1\\d\\d|[1-9]\\d|\\d)){3}|:[a-fA-F\\d]{1,4}|:)|(?:[a-fA-F\\d]{1,4}:){5}(?::(?:25[0-5]|2[0-4]\\d|1\\d\\d|[1-9]\\d|\\d)(?:\\.(?:25[0-5]|2[0-4]\\d|1\\d\\d|[1-9]\\d|\\d)){3}|(?::[a-fA-F\\d]{1,4}){1,2}|:)|(?:[a-fA-F\\d]{1,4}:){4}(?:(?::[a-fA-F\\d]{1,4}){0,1}:(?:25[0-5]|2[0-4]\\d|1\\d\\d|[1-9]\\d|\\d)(?:\\.(?:25[0-5]|2[0-4]\\d|1\\d\\d|[1-9]\\d|\\d)){3}|(?::[a-fA-F\\d]{1,4}){1,3}|:)|(?:[a-fA-F\\d]{1,4}:){3}(?:(?::[a-fA-F\\d]{1,4}){0,2}:(?:25[0-5]|2[0-4]\\d|1\\d\\d|[1-9]\\d|\\d)(?:\\.(?:25[0-5]|2[0-4]\\d|1\\d\\d|[1-9]\\d|\\d)){3}|(?::[a-fA-F\\d]{1,4}){1,4}|:)|(?:[a-fA-F\\d]{1,4}:){2}(?:(?::[a-fA-F\\d]{1,4}){0,3}:(?:25[0-5]|2[0-4]\\d|1\\d\\d|[1-9]\\d|\\d)(?:\\.(?:25[0-5]|2[0-4]\\d|1\\d\\d|[1-9]\\d|\\d)){3}|(?::[a-fA-F\\d]{1,4}){1,5}|:)|(?:[a-fA-F\\d]{1,4}:){1}(?:(?::[a-fA-F\\d]{1,4}){0,4}:(?:25[0-5]|2[0-4]\\d|1\\d\\d|[1-9]\\d|\\d)(?:\\.(?:25[0-5]|2[0-4]\\d|1\\d\\d|[1-9]\\d|\\d)){3}|(?::[a-fA-F\\d]{1,4}){1,6}|:)|(?::(?:(?::[a-fA-F\\d]{1,4}){0,5}:(?:25[0-5]|2[0-4]\\d|1\\d\\d|[1-9]\\d|\\d)(?:\\.(?:25[0-5]|2[0-4]\\d|1\\d\\d|[1-9]\\d|\\d)){3}|(?::[a-fA-F\\d]{1,4}){1,7}|:)))(?:%[0-9a-zA-Z]{1,})?$)$/.test(
      ip
    );
  }

  /**
   * 是否是url
   * @param path
   */
  isUrl(path: string): boolean {
    return /(((^https?:(?:\/\/)?)(?:[-;:&=\+\$,\w]+@)?[A-Za-z0-9.-]+(?::\d+)?|(?:www.|[-;:&=\+\$,\w]+@)[A-Za-z0-9.-]+)((?:\/[\+~%\/.\w-_]*)?\??(?:[-\+=&;%@.\w_]*)#?(?:[\w]*))?)$/.test(
      path
    );
  }

  /**
   * 是否中文
   * @param value 测试字符串
   */
  isChinese(value: string): boolean {
    return /^[\u4e00-\u9fa5]+$/.test(value);
  }

  /**
   * 是否为数值
   * @param value
   * @param minus 是否允许负数
   * @param fraction 最大保留小数位数
   */
  isNum(value: string, minus?: boolean, fraction?: number): boolean {
    const regex =
      (minus ? '^-?' : '^') +
      `(([1-9]{1}\\d*)|(0{1}))` +
      (fraction && fraction > 0 ? `(\\.\\d{1,${fraction}})?$` : '$');
    return new RegExp(regex).test(value);
  }

  /**
   * 是否为邮箱
   * @param value
   */
  isEmail(value: string) {
    return /^([a-z0-9_\.-]+)@([\da-z\.-]+)\.([a-z\.]{2,6})$/.test(value);
  }

  /**
   * 是否是纯对象值
   * @param obj
   */
  isPlainObject<T = NzSafeAny>(obj: T) {
    if (!obj || toStr.call(obj) !== '[object Object]') {
      return false;
    }

    const hasOwnConstructor = hasOwn.call(obj, 'constructor');
    const hasIsPrototypeOf =
      // @ts-ignore
      obj.constructor && obj.constructor.prototype && hasOwn.call(obj.constructor.prototype, 'isPrototypeOf');
    // @ts-ignore
    if (obj.constructor && !hasOwnConstructor && !hasIsPrototypeOf) {
      return false;
    }

    let key;
    for (key in obj) {
      /**/
    }

    return typeof key === 'undefined' || hasOwn.call(obj, key);
  }

  /**
   * 转换为日期
   * @param date 待转换的值
   */
  toDate(date?: Date | string | number): Date | null {
    if (date == null) {
      return null;
    }
    if (this.isDate(date)) {
      return date;
    }
    if (this.isNumber(date)) {
      return new Date(date);
    }
    if (this.isString(date)) {
      const value = Date.parse(date);
      if (isNaN(value)) {
        const match = date.match(/\d+/g);
        if (match) {
          // @ts-ignore
          return new Date(...match);
        }
      }
    }
    return null;
  }

  /**
   * 合并对象到FormData
   * @param obj 要合并的对象
   * @param {FormData} form 表单
   */
  mergeFormData<T>(form: FormData, obj: T): void {
    const list = this.serializeParams(obj);
    for (const it of list) {
      form.append(it.key, it.value);
    }
  }

  /**
   * 把js对象序列化对请求数据,格式为 a=av&b=bv...&n=nv
   * @param obj
   * @returns
   */
  serialize<T>(obj: T): string {
    const list = this.serializeParams<T>(obj);
    let query = '';
    for (const it of list) {
      query += encodeURIComponent(it.key) + '=' + encodeURIComponent(it.value) + '&';
    }
    return query === '' ? query : query.substr(0, query.length - 1);
  }

  /**
   * 把js对象序列化对请求数据
   * @param obj
   * @returns
   */
  serializeParams<T>(obj: T): Record<string, NzSafeAny>[] {
    if (!obj) {
      return [];
    }

    const query: Record<string, NzSafeAny>[] = [];
    this.buildParam(obj, query);
    return query;
  }

  private buildParam<T extends Record<string, NzSafeAny>>(inObj: T, list: Record<string, NzSafeAny>[]) {
    let value: NzSafeAny;
    let subName: string;
    let innerObj: Record<string, string>;
    for (const name in inObj) {
      value = inObj[name];
      if (value instanceof Array) {
        for (let i = 0; i < value.length; ++i) {
          if (value[i] instanceof Array || value[i] instanceof Object) {
            if (value[i].toJSON) {
              list.push({ key: name, value: value[i].toJSON() });
              continue;
            }
            innerObj = {};
            innerObj[name + '[' + i + ']'] = value[i];
            this.buildParam(innerObj, list);
          } else if (value[i] !== undefined && value[i] !== null) {
            list.push({ key: name, value: value[i] });
          }
        }
        continue;
      }
      if (value instanceof Object) {
        if (value.toJSON) {
          list.push({ key: name, value: value.toJSON() });
          continue;
        }

        for (subName of Object.keys(value)) {
          innerObj = {};
          innerObj[name + '.' + subName] = value[subName];
          this.buildParam(innerObj, list);
        }
        continue;
      }
      if (value !== undefined && value !== null) {
        list.push({ key: name, value });
      }
    }
  }
}

