import { Directive, EmbeddedViewRef, Input, OnDestroy, TemplateRef, ViewContainerRef } from '@angular/core';
import { of, Subscription } from 'rxjs';
import { NzxAntdService } from '@xmagic/nzx-antd';
import { NzSafeAny } from 'ng-zorro-antd/core/types'

/**
 * 权限指令, 有权限渲染模板,否则渲染else模板
 * @example
 * <button *auth="['code1', 'code2']; else notAuth">有权限时显示</button>
 * <button *auth="'code1'; else notAuth">有权限时显示</button>
 * <ng-template #notAuth>没有权限时显示</ng-template>
 */
@Directive({
  selector: '[auth]'
})
export class AuthDirective<T = unknown> implements OnDestroy {
  /** @internal */
  static authUseIfTypeGuard: void;
  static ngTemplateGuard_auth: 'binding';
  private readonly changeSubscription?: Subscription;
  private authSubscription?: Subscription;

  protected _context: AuthContext<T> = new AuthContext<T>();
  protected _thenTemplateRef: TemplateRef<AuthContext<T>> | null = null;
  protected _elseTemplateRef: TemplateRef<AuthContext<T>> | null = null;
  protected _thenViewRef: EmbeddedViewRef<AuthContext<T>> | null = null;
  protected _elseViewRef: EmbeddedViewRef<AuthContext<T>> | null = null;

  /**
   *
   * 为 `auth` 将要渲染的模板确保正确的上下文类型。
   * 该方法用于向 Ivy 模板类型检查编译器发出信号，即 `auth` 结构化指令会使用特定的上下文类型渲染其模板。
   *
   */
  static ngTemplateContextGuard<T>(
    dir: AuthDirective<T>,
    ctx: NzSafeAny
  ): ctx is AuthContext<Exclude<T, false | 0 | '' | null | undefined>> {
    return true;
  }

  protected hasAuth: Required<NzxAntdService>['hasAuth'] = () => of(true);

  constructor(
    protected _viewContainer: ViewContainerRef,
    public templateRef: TemplateRef<AuthContext<T>>,
    public antdService: NzxAntdService
  ) {
    this._thenTemplateRef = templateRef;

    if (this.antdService.authChange) {
      this.changeSubscription = this.antdService.authChange().subscribe(() => this._updateView());
    }

    if (this.antdService.hasAuth) {
      this.hasAuth = this.antdService.hasAuth;
    }
  }

  /**
   * 布尔表达式，将其作为显示模板的条件进行计算。
   *
   */
  @Input()
  set auth(condition: T) {
    this._context.$implicit = this._context.auth = condition;
    this._updateView();
  }

  /**
   * 当此条件表达式计算为 true 时要显示的模板。
   */
  @Input()
  set authThen(templateRef: TemplateRef<AuthContext<T>> | null) {
    assertTemplate('authThen', templateRef);
    this._thenTemplateRef = templateRef;
    this._thenViewRef = null; // clear previous view if any.
    this._updateView();
  }

  /**
   * 当此条件表达式计算为 false 时要显示的模板。
   */
  @Input()
  set authElse(templateRef: TemplateRef<AuthContext<T>> | null) {
    assertTemplate('authElse', templateRef);
    this._elseTemplateRef = templateRef;
    this._elseViewRef = null; // clear previous view if any.
    this._updateView();
  }

  protected _updateView() {
    if (this.authSubscription) {
      this.authSubscription.unsubscribe();
      this.authSubscription = undefined;
    }
    this.authSubscription = this.hasAuth(this._context.$implicit).subscribe(auth => {
      if (this.canRender(auth, this._context.$implicit)) {
        if (!this._thenViewRef) {
          this._viewContainer.clear();
          this._elseViewRef = null;
          if (this._thenTemplateRef) {
            this._thenViewRef = this._viewContainer.createEmbeddedView(this._thenTemplateRef, this._context);
          }
        }
      } else {
        if (!this._elseViewRef) {
          this._viewContainer.clear();
          this._thenViewRef = null;
          if (this._elseTemplateRef) {
            this._elseViewRef = this._viewContainer.createEmbeddedView(this._elseTemplateRef, this._context);
          }
        }
      }
    });
  }

  /**
   * 是否允许渲染
   * @param hasAuth 是否有权限
   * @param value 权限码
   * @protected
   */
  protected canRender(hasAuth: boolean, value: T) {
    return hasAuth;
  }

  ngOnDestroy(): void {
    if (this.changeSubscription) {
      this.changeSubscription.unsubscribe();
    }
    if (this.authSubscription) {
      this.authSubscription.unsubscribe();
    }
  }
}

/**
 * @publicApi
 */
export class AuthContext<T = unknown> {
  public $implicit: T = null!;
  public auth: T = null!;
}

function assertTemplate(property: string, templateRef: TemplateRef<NzSafeAny> | null): void {
  const isTemplateRefOrNull = !!(!templateRef || templateRef.createEmbeddedView);
  if (!isTemplateRefOrNull) {
    throw new Error(`${property} must be a TemplateRef, but received '${templateRef}'.`);
  }
}

import { Directive, Input, TemplateRef, ViewContainerRef } from '@angular/core';
import { NzxAntdService } from '@xmagic/nzx-antd';
import { AuthContext, AuthDirective } from './auth.directive';

/**
 * 权限指令, 无权限渲染模板,否则渲染else模板
 * @example
 * <button *auth.not="['code1', 'code2']; else hasAuth">没有有权限时显示</button>
 * <button *auth.not="'code1'; else hasAuth">没有有权限时显示</button>
 * <ng-template #hasAuth>有权限时显示</ng-template>
 */
@Directive({
  selector: '[auth.not]'
})
export class AuthNotDirective<T = unknown> extends AuthDirective {
  /** @internal */
  static authNotUseIfTypeGuard: void;
  static ngTemplateGuard_authNot: 'binding';

  /**
   * 布尔表达式，将其作为显示模板的条件进行计算。
   *
   */
  @Input('auth.not')
  set authNot(condition: T) {
    super.auth = condition;
  }

  /**
   * 当此条件表达式计算为 true 时要显示的模板。
   */
  @Input('auth.notThen')
  set authNotThen(templateRef: TemplateRef<AuthContext<T>> | null) {
    super.authThen = templateRef;
  }

  /**
   * 当此条件表达式计算为 false 时要显示的模板。
   */
  @Input('auth.notElse')
  set authNotElse(templateRef: TemplateRef<AuthContext<T>> | null) {
    super.authElse = templateRef;
  }

  /**
   * 是否允许渲染, 没有权限的时候渲染
   * @param hasAuth 是否有权限
   * @param value 权限码
   * @protected
   */
  protected override canRender(hasAuth: boolean, value: T) {
    return !hasAuth;
  }

  constructor(
    protected override _viewContainer: ViewContainerRef,
    public override templateRef: TemplateRef<AuthContext<T>>,
    public override antdService: NzxAntdService
  ) {
    super(_viewContainer, templateRef, antdService);
  }
}

import { Directive, EventEmitter, HostListener, Input, Output } from '@angular/core';
import { InputBoolean } from 'ng-zorro-antd/core/util';

/**
 * 防止重复触发点击
 * @example
 * <button (click.once)="myMethod($event)" [delay]="1000" (originClick)="method($event)">按钮</button>
 */
@Directive({
  selector: '[click.once]'
})
export class ClickOnceDirective {
  constructor() {}

  /**
   * 点击延迟时间(单位:ms)
   */
  @Input() delay = 500;
  @Input() @InputBoolean() disabled = false;
  /**
   * neClick事件
   */
  @Output('click.once') clickEvent = new EventEmitter<MouseEvent>();
  /**
   * 原始点击事件
   * delay - 是否处于延迟状态
   */
  @Output() originClick = new EventEmitter<{ target: MouseEvent; delay: boolean }>();
  private _delayDisabled = false;

  @HostListener('click', ['$event'])
  clickEventHandle(event: MouseEvent): void {
    if (this.disabled) {
      return;
    }

    this.originClick.emit({ target: event, delay: this._delayDisabled });
    if (this._delayDisabled) {
      event.preventDefault();
      event.stopPropagation();
      return;
    }

    this._delayDisabled = true;
    setTimeout(() => (this._delayDisabled = false), this.delay);
    this.clickEvent.emit(event);
  }

}

import {
  Directive,
  ElementRef,
  EventEmitter,
  Inject,
  Input,
  OnChanges,
  OnDestroy,
  OnInit,
  Output,
  PLATFORM_ID,
  SimpleChanges,
  NgZone
} from '@angular/core';
import { isPlatformBrowser } from '@angular/common';

@Directive({
  selector: '[click.outside]',
  exportAs: 'clickOutside'
})
export class ClickOutsideDirective implements OnInit, OnChanges, OnDestroy {
  @Input() clickOutsideEnabled = true;

  @Input() attachOutsideOnClick = false;
  @Input() delayClickOutsideInit = false;
  @Input() emitOnBlur = false;

  @Input() exclude?: string;
  @Input() excludeBeforeClick?: boolean;

  @Input() clickOutsideEvents?: string;

  @Output('click.outside') clickOutside: EventEmitter<Event> = new EventEmitter<Event>();

  private _nodesExcluded: Array<HTMLElement> = [];
  private _events: Array<string> = ['click'];

  constructor(private _el: ElementRef, private _ngZone: NgZone, @Inject(PLATFORM_ID) private platformId: Object) {
    this._initOnClickBody = this._initOnClickBody.bind(this);
    this._onClickBody = this._onClickBody.bind(this);
    this._onWindowBlur = this._onWindowBlur.bind(this);
  }

  ngOnInit() {
    if (!isPlatformBrowser(this.platformId)) {
      return;
    }

    this._init();
  }

  ngOnDestroy() {
    if (!isPlatformBrowser(this.platformId)) {
      return;
    }

    this._removeClickOutsideListener();
    this._removeAttachOutsideOnClickListener();
    this._removeWindowBlurListener();
  }

  ngOnChanges(changes: SimpleChanges) {
    if (!isPlatformBrowser(this.platformId)) {
      return;
    }

    if (changes['attachOutsideOnClick'] || changes['exclude'] || changes['emitOnBlur']) {
      this._init();
    }
  }

  private _init() {
    if (this.clickOutsideEvents != null) {
      this._events = this.clickOutsideEvents!.split(',').map(e => e.trim());
    }

    this._excludeCheck();

    if (this.attachOutsideOnClick) {
      this._initAttachOutsideOnClickListener();
    } else {
      this._initOnClickBody();
    }

    if (this.emitOnBlur) {
      this._initWindowBlurListener();
    }
  }

  private _initOnClickBody() {
    if (this.delayClickOutsideInit) {
      setTimeout(this._initClickOutsideListener.bind(this));
    } else {
      this._initClickOutsideListener();
    }
  }

  private _excludeCheck() {
    if (this.exclude) {
      try {
        const nodes = Array.from(document.querySelectorAll(this.exclude)) as Array<HTMLElement>;
        if (nodes) {
          this._nodesExcluded = nodes;
        }
      } catch (err) {
        console.error('[ng-click-outside] Check your exclude selector syntax.', err);
      }
    }
  }

  private _onClickBody(ev: Event) {
    if (!this.clickOutsideEnabled) {
      return;
    }

    if (this.excludeBeforeClick) {
      this._excludeCheck();
    }

    if (!this._el.nativeElement.contains(ev.target) && !this._shouldExclude(ev.target as HTMLElement)) {
      this._emit(ev);

      if (this.attachOutsideOnClick) {
        this._removeClickOutsideListener();
      }
    }
  }

  /**
   * Resolves problem with outside click on iframe
   * @see https://github.com/arkon/ng-click-outside/issues/32
   */
  private _onWindowBlur(ev: Event) {
    setTimeout(() => {
      if (!document.hidden) {
        this._emit(ev);
      }
    });
  }

  private _emit(ev: Event) {
    if (!this.clickOutsideEnabled) {
      return;
    }

    this._ngZone.run(() => this.clickOutside.emit(ev));
  }

  private _shouldExclude(target: HTMLElement): boolean {
    for (const excludedNode of this._nodesExcluded) {
      if (excludedNode.contains(target)) {
        return true;
      }
    }
    return false;
  }

  private _initClickOutsideListener() {
    this._ngZone.runOutsideAngular(() => {
      this._events.forEach(e => document.addEventListener(e, this._onClickBody));
    });
  }

  private _removeClickOutsideListener() {
    this._ngZone.runOutsideAngular(() => {
      this._events.forEach(e => document.removeEventListener(e, this._onClickBody));
    });
  }

  private _initAttachOutsideOnClickListener() {
    this._ngZone.runOutsideAngular(() => {
      this._events.forEach(e => this._el.nativeElement.addEventListener(e, this._initOnClickBody));
    });
  }

  private _removeAttachOutsideOnClickListener() {
    this._ngZone.runOutsideAngular(() => {
      this._events.forEach(e => this._el.nativeElement.removeEventListener(e, this._initOnClickBody));
    });
  }

  private _initWindowBlurListener() {
    this._ngZone.runOutsideAngular(() => {
      window.addEventListener('blur', this._onWindowBlur);
    });
  }

  private _removeWindowBlurListener() {
    this._ngZone.runOutsideAngular(() => {
      window.removeEventListener('blur', this._onWindowBlur);
    });
  }
}

import { Directive, HostListener, Input } from '@angular/core';
import { DownloadOption, NzxDownloadService } from '@xmagic/nzx-antd/service';
import { FetchOptions } from '@xmagic/nzx-antd/service';

@Directive({
  selector: '[down-file]',
  exportAs: 'downFile'
})
export class DownFileDirective {
  /**
   * 下载文件的url
   */
  @Input('down-file') url!: string;
  /**
   * 是否禁用点击下载
   */
  @Input() disabled = false;
  /**
   * 请求方式
   */
  @Input() method: FetchOptions['method'] = 'get';
  /**
   * 请求参数
   */
  @Input() data?: FetchOptions['data'];
  /**
   * 发送之前的回调函数
   */
  @Input() beforeSend?: FetchOptions['beforeSend'];

  /**
   * 请求完成后的回调
   */
  @Input() afterDownload?: DownloadOption['afterDownload'];

  /**
   * 下载并保存完成的回调
   */
  @Input() downloadDone?: DownloadOption['downloadDone'];

  constructor(private downloadService: NzxDownloadService) {}

  @HostListener('click', ['$event'])
  clickEventHandler(event: MouseEvent) {
    event.preventDefault();
    event.stopPropagation();

    if (this.disabled) {
      return;
    }
    this.downloadService.download({
      url: this.url,
      method: this.method,
      data: this.data,
      beforeSend: this.beforeSend,
      afterDownload: this.afterDownload,
      downloadDone: this.downloadDone
    });
  }
}

import { Directive, ElementRef, Input, OnChanges, OnInit, Renderer2, SimpleChange, SimpleChanges } from '@angular/core';

/**
 * font-awesome 使用指令
 * @
 * <i
 */
@Directive({
  selector: '[fa-icon]',
  exportAs: 'faIcon',
  host: {
    '[class.fa]': `type === 'fa'`,
    '[class.far]': `type === 'far'`,
    '[class.fa-spin]': 'spin === true',
    '[class.fa-pulse]': 'pulse === true',
    '[class.fa-fw]': 'fixedWidth === true',
    '[class.fa-border]': 'border === true',
    '[class.fa-inverse]': 'inverse === true',
    '[class.fa-flip-horizontal]': `flip === 'horizontal'`,
    '[class.fa-flip-vertical]': `flip === 'vertical'`,
    '[class.fa-flip-both]': `flip === 'both'`,
    '[class.fa-pull-left]': `pull === 'left'`,
    '[class.fa-pull-right]': `pull === 'right'`,
    '[class.fa-rotate-90]': `rotate === 90`,
    '[class.fa-rotate-180]': `rotate === 180`,
    '[class.fa-rotate-270]': `rotate === 270`,
    '[class.fa-stack-1x]': `stackItemSize === '1x'`,
    '[class.fa-stack-2x]': `stackItemSize === '2x'`
  }
})
export class FaIconDirective implements OnInit, OnChanges {
  @Input('fa-icon') icon!: string;
  @Input() type: 'far' | 'fa' = 'fa';
  @Input() spin?: boolean;
  @Input() pulse?: boolean;
  @Input() flip?: 'horizontal' | 'vertical' | 'both';
  @Input() pull?: 'left' | 'right';
  @Input() border?: boolean;
  @Input() inverse?: boolean;
  @Input() symbol?: string | boolean;
  @Input() rotate?: 90 | 180 | 270;
  @Input() fixedWidth?: boolean;
  @Input() stackItemSize?: '1x' | '2x';
  @Input() size?: IconSizeType;

  constructor(public render: Renderer2, public element: ElementRef) {}

  ngOnInit(): void {
    this.renderIcon(this.icon);
    this.renderSize(this.size);
  }

  renderIcon(newIcon: string | null, oldIcon?: string | null) {
    if (oldIcon) {
      this.getIconClass(oldIcon).forEach(cls => this.render.removeClass(this.element.nativeElement, cls));
    }
    if (newIcon) {
      this.getIconClass(newIcon).forEach(cls => this.render.addClass(this.element.nativeElement, cls));
    }
  }

  getIconClass(icon: string): string[] {
    return icon ? icon.split(/\s/) : [];
  }

  renderSize(newSize?: IconSizeType, oldSize?: IconSizeType) {
    const newSizeCls = newSize ? `fa-${newSize}` : null;
    const oldSizeCls = oldSize ? `fa-${oldSize}` : null;
    this.renderIcon(newSizeCls, oldSizeCls);
  }

  ngOnChanges(changes: { [K in keyof this]?: SimpleChange } & SimpleChanges): void {
    if (changes.icon && !changes.icon.isFirstChange()) {
      this.renderIcon(changes.icon.currentValue, changes.icon.previousValue);
    }

    if (changes.size && !changes.size.isFirstChange()) {
      this.renderSize(changes.size.currentValue, changes.size.previousValue);
    }
  }
}

export type IconSizeType = 'xs' | 'lg' | 'sm' | '1x' | '2x' | '3x' | '4x' | '5x' | '6x' | '7x' | '8x' | '9x' | '10x';

import { Directive, Inject, Input, TemplateRef, ViewContainerRef } from '@angular/core';
import { NzSafeAny } from 'ng-zorro-antd/core/types';

/**
 * 允许在模板内复用计算值（包含异步），避免重复重新计算。
 * @example
 *
 * <div *let="value1 as v">
 *   <p>{{ v }}</p>
 * </div>
 * <div *let="time$ | async as time">
 *   <p>{{ time }}</p>
 * </div>
 */
@Directive({ selector: '[let]' })
export class LetDirective<T> {
  @Input() let!: T;

  constructor(@Inject(ViewContainerRef) vc: ViewContainerRef, @Inject(TemplateRef) ref: TemplateRef<LetContext<T>>) {
    vc.createEmbeddedView(ref, new LetContext<T>(this));
  }

  static ngTemplateContextGuard<T>(_dir: LetDirective<T>, _ctx: NzSafeAny): _ctx is LetDirective<T> {
    return true;
  }
}

export class LetContext<T> {
  constructor(private readonly directive: LetDirective<T>) {}

  get $implicit(): T {
    return this.directive.let;
  }

  get let(): T {
    return this.directive.let;
  }
}

