import { Directive, Input, OnInit, TemplateRef } from '@angular/core';
import { NzSafeAny } from 'ng-zorro-antd/core/types';

/**
 * 获取模板名称
 * @example
 * ``` html
 * <ng-template named="test"></ng-template>
 * <ng-template #test named></ng-template>
 *
 * ```
 * ``` javascript
 * @Component(...)
 * export class TestComponent {
 *   @ViewChildren(NamedTemplate) list!: QueryList<NamedTemplate>;
 *
 *   trace() {
 *     this.list.forEach(it => {
 *       console.log(it.named);
 *       console.log(it.template);
 *     });
 *   }
 * }
 * ```
 */
@Directive({
  selector: 'ng-template[named]',
  exportAs: 'namedTemplate'
})
export class NamedTemplate<T> implements OnInit {
  /**
   * 模板名称
   */
  @Input() named!: string;
  constructor(public template: TemplateRef<T>) {}

  ngOnInit(): void {
    this.resolveName();
  }

  resolveName() {
    if (!this.named && this.template) {
      const tplRef = this.template as NzSafeAny;
      // localNames为数组, 如果没有name则为null
      this.named = tplRef._declarationTContainer.localNames?.[0];
    }
  }
}

import { Directive, Host } from '@angular/core';
import { NzModalComponent } from 'ng-zorro-antd/modal';
import { NzxModalDragService } from './modal-drag.service';

/**
 * 可拖动的对话框
 * @example
 * ``` html
 * <nz-modal nzxModalDrag ></nz-modal>
 ```
 */
@Directive({
  selector: 'nz-modal[nzxModalDrag]'
})
export class NzxModalDragDirective {
  constructor(@Host() protected modal: NzModalComponent, public modalDragService: NzxModalDragService) {
    const wrapCls = this.modalDragService.getRandomCls();
    modal.afterOpen.subscribe(() => {
      const modelElement = modal.getElement()!;
      if (!modelElement || modelElement.className.indexOf(NzxModalDragService.DRAG_CLS_PREFIX) !== -1) {
        return;
      }

      modelElement.classList.add(wrapCls);
      const drag = this.modalDragService.createDragHandler(wrapCls, modal.nzMask, modal.nzModalType);
      modal.afterClose.subscribe(() => {
        if (drag && !drag.dropped) {
          drag.dispose();
        }
      });
    });
  }
}

