import {
  ChangeDetectionStrategy,
  ChangeDetectorRef,
  Component,
  EventEmitter,
  forwardRef,
  Input,
  OnChanges,
  OnInit,
  Output,
  SimpleChange,
  SimpleChanges,
  ViewEncapsulation
} from '@angular/core';
import { ControlValueAccessor, NG_VALUE_ACCESSOR } from '@angular/forms';
import { BaseControl } from '@xmagic/nzx-antd/util';
import { NzSafeAny } from 'ng-zorro-antd/core/types'

@Component({
  selector: 'nzx-checkbox',
  templateUrl: './checkbox.component.html',
  preserveWhitespaces: false,
  encapsulation: ViewEncapsulation.None,
  changeDetection: ChangeDetectionStrategy.OnPush,
  providers: [
    {
      provide: NG_VALUE_ACCESSOR,
      useExisting: forwardRef(() => NzxCheckboxComponent),
      multi: true
    }
  ]
})
export class NzxCheckboxComponent<T> extends BaseControl<T[]> implements ControlValueAccessor, OnInit, OnChanges {
  @Input() nzxOptions: NzxCheckboxOption<T>[] = [];
  @Input() override nzxDisabled = false;
  @Output() nzxFocus = new EventEmitter<NzxCheckboxOption<T>>();
  @Output() nzxBlur = new EventEmitter<NzxCheckboxOption<T>>();
  nzxValue: T[] = [];
  constructor(protected cdr: ChangeDetectorRef) {
    super();
  }

  ngOnInit(): void {
    this.nzxValue = this.nzxOptions.filter(v => v.checked).map(v => v.value);
  }

  ngOnChanges(changes: { [K in keyof this]?: SimpleChange } & SimpleChanges): void {
    if (changes.nzxOptions && !changes.nzxOptions.isFirstChange()) {
      this.writeValue(this.nzxValue);
    }
  }

  ngModelChange(values: T[]) {
    this.nzxValue = values;
    this.onTouched();
    this.onChange(this.nzxValue);
  }

  writeValue(value: T[] | null): void {
    this.nzxValue = value == null ? [] : Array.isArray(value) ? value : [value];
    if (this.nzxOptions && this.nzxOptions.length) {
      this.nzxOptions.forEach(v => (v.checked = this.nzxValue.indexOf(v.value) !== -1));
    }
    this.cdr.markForCheck();
  }
}

/**
 * 数据配置项
 */
export interface NzxCheckboxOption<T =  NzSafeAny> {
  /**
   *
   */
  label: string;
  /**
   * 值
   */
  value: T;
  /**
   * 是否禁用
   */
  disabled?: boolean;
  /**
   * 是否隐藏
   */
  hide?: boolean;
  /**
   * 是否半选
   */
  indeterminate?: boolean;
  /**
   * 值变化的回调函数
   * @param checked
   */
  ngModelChange?: (checked: boolean) => void;

  [key: string]: NzSafeAny;
}

export type OptionItem = Omit<NzxCheckboxOption, 'indeterminate' | 'ngModelChange'>;

import {
  ChangeDetectionStrategy,
  Component,
  ElementRef,
  EventEmitter,
  Input,
  OnInit,
  Output,
  Renderer2,
  ViewEncapsulation
} from '@angular/core';
import { moveItemInArray } from '@angular/cdk/drag-drop';
import { CdkDragDrop, CdkDragRelease, CdkDragStart } from '@angular/cdk/drag-drop/drag-events';
import { NzSafeAny } from 'ng-zorro-antd/core/types';
import { NzxColumn } from '../../table.type';

/**
 * 列设置
 * 注意: 只在表头不分组的情况下有效
 */
@Component({
  selector: 'nzx-column-setting',
  templateUrl: './column-setting.component.html',
  preserveWhitespaces: false,
  encapsulation: ViewEncapsulation.None,
  changeDetection: ChangeDetectionStrategy.OnPush
})
export class NzxColumnSettingComponent<T> implements OnInit /*, OnChanges*/ {
  _nzxColumns: NzxColumn<T>[] = [];
  _columnNameChecked!: boolean | null;
  _indeterminate!: boolean | null;

  /**
   * 拖拽预览样式
   */
  @Input() dragPreviewClass = 'nzx-column-setting__drag-preview';
  /**
   * 显示列名
   */
  @Input() columnNameVisible: boolean | null = true;

  @Input() set nzxColumns(value: NzxColumn<T>[]) {
    if (value) {
      this._nzxColumns = value;
      this.refreshNameCheckedStatus();
    }
  }
  get nzxColumns() {
    return this._nzxColumns;
  }

  /**
   * 当个列选中事件
   */
  @Output() columnCheckedChange = new EventEmitter<NzxColumn<T>>();
  /**
   * 排序列触发
   */
  @Output() sortedColumn = new EventEmitter<CdkDragDrop<NzxColumn<T>, NzSafeAny>>();
  /**
   * 列名选择Change
   */
  @Output() columnNameCheckedChange = new EventEmitter<boolean>();

  /**
   * 固定列
   */
  @Output() fixedClick = new EventEmitter<NzxColumn<T>>();

  constructor(protected renderer: Renderer2) {}

  ngOnInit(): void {}

  columnVisible(item: NzxColumn<T>, checked: boolean) {
    this.refreshNameCheckedStatus();
    this.columnCheckedChange.emit(item);
  }

  /**
   * 固定列
   * @param column
   * @param fixed
   */
  fixedColumn(column: NzxColumn<T>, fixed?: 'left' | 'right') {
    if (column.fixed === fixed) {
      return;
    }
    column.fixed = fixed;
    this.fixedClick.emit(column);
  }

  /**
   * 拖动列排序
   * @param event
   * @param list 排序数组
   */
  dropColumn(event: CdkDragDrop<NzxColumn<T>, NzSafeAny>, list: NzxColumn<T>[]) {
    moveItemInArray(list, event.previousIndex, event.currentIndex);
    this.sortedColumn.emit(event);
  }

  /**
   * 开始拖动
   * @param event
   */
  cdkDragStarted(event: CdkDragStart) {
    const preview = new ElementRef<HTMLElement>(document.querySelector('.cdk-drag.cdk-drag-preview')!);
    this.renderer.addClass(preview.nativeElement, this.dragPreviewClass);
  }

  /**
   * 结束拖动
   * @param event
   */
  cdkDragReleased(event: CdkDragRelease) {
    const preview = new ElementRef<HTMLElement>(document.querySelector('.cdk-drag.cdk-drag-preview')!);
    this.renderer.removeClass(preview.nativeElement, this.dragPreviewClass);
  }

  /**
   * 展示列复选框 checked
   * @param checked
   */
  columnNameChange(checked: boolean) {
    if (this._nzxColumns && this._nzxColumns.length) {
      this._columnNameChecked = checked;
      this._indeterminate = false;
      this._nzxColumns.filter(v => v.settingVisible && v.settingDisabled !== true).forEach(v => (v.visible = checked));
      this.columnNameCheckedChange.emit(checked);
    }
  }

  /**
   * 更新展示列状态
   */
  refreshNameCheckedStatus(): void {
    if (this._nzxColumns && this._nzxColumns.length) {
      const list = this._nzxColumns.filter(v => v.settingVisible && v.settingDisabled !== true);
      this._columnNameChecked = list.every(item => item.visible);
      this._indeterminate = !this._columnNameChecked && list.some(item => item.visible);
    } else {
      this._columnNameChecked = false;
      this._indeterminate = false;
    }
  }
}

import { Component, HostBinding, Input, OnInit } from '@angular/core';

@Component({
  selector: 'nzx-content',
  template: '<ng-content></ng-content>',
  host: {
    '[class.nzx-page-content]': 'true'
  }
})
export class NzxContentComponent {
  /**
   * 显示验证占位符(是否显示form-item底部的空白)
   */
  @Input() formItemBottomVisible = true;
  /**
   * margin
   */
  @Input() margin?: string;
  /**
   * margin top
   */
  @Input() marginTop = '10px';

  @HostBinding('class.hide-form-item-bottom') get hideFormItemBottom() {
    return !this.formItemBottomVisible;
  }
  @HostBinding('style.margin') get contentMargin() {
    return this.margin;
  }
  @HostBinding('style.margin-top') get contentMarginTop() {
    return this.marginTop;
  }
  constructor() {}
}

import { Component, HostBinding, Input } from '@angular/core';
import { isContentEmpty } from './is-content-empty';

@Component({
  selector: 'nzx-header',
  template: `
    <div class="page-header_row">
      <div class="page-header_col">
        <ng-content></ng-content>
      </div>
      <div
        #divElement
        (cdkObserveContent)="contentChanged(divElement)"
        [debounce]="200"
        [class.header-content-wrapper]="hasContent"
      >
        <ng-content select="[buttons]"></ng-content>
      </div>
    </div>
  `,
  host: {
    '[class.nzx-page-header]': 'true'
  }
})
export class NzxHeaderComponent {
  hasContent = false;
  /**
   * 显示验证占位符(是否显示form-item底部的空白)
   */
  @Input() hasBottom = false;
  /**
   * 是否显示按钮区域
   */
  @Input() buttonsVisible = true;
  @HostBinding('class.hide-form-item-bottom') get hideFormItemBottom() {
    return !this.hasBottom;
  }

  constructor() {}

  contentChanged(element: HTMLElement) {
    this.hasContent = !isContentEmpty(element);
  }
}

import { Component } from '@angular/core';

@Component({
  selector: 'nzx-page',
  template: '<ng-content></ng-content>',
  host: {
    '[class.nzx-page]': 'true'
  }
})
export class NzxPageComponent {
  constructor() {}
}

